            {
                KissMsg.ShowInfo("Für das Verbuchen ist die KtoNr. des Sparhafen-Kontos nötig");
                return;
            }

            SqlQuery qry = DBUtil.OpenSQL(@"
                exec spKgSparhafenAbschlussBuchung {0},{1}

                select BaPersonID, Fehlermeldung, KgBuchungID
                from KgSparhafenAbschluss
                where KgSparhafenAbschlussID = {0}",
                qryQuery["KgSparhafenAbschlussID$"],
                edtKtoNr.EditValue);

            qryQuery["BaPersonID$"]   = qry["BaPersonID"];
            qryQuery["Fehlermeldung"] = qry["Fehlermeldung"];
            qryQuery["Beleg"]         = qry["KgBuchungID"];
            qryQuery.RowModified = false;
            qryQuery.Row.AcceptChanges();

            btnVerbuchen.Enabled =  !DBUtil.IsEmpty(qryQuery["Fehlermeldung"]) &&
                                    !DBUtil.IsEmpty(qryQuery["BaPersonID$"]);
        }

        private void qryQuery_PositionChanged(object sender, System.EventArgs e)
        {
            btnVerbuchen.Enabled = !DBUtil.IsEmpty(qryQuery["Fehlermeldung"]) && !DBUtil.IsEmpty(qryQuery["BaPersonID$"]);
        }

        #endregion
    }
}