        #region Constructors

        public CtlQueryMigKissWVTraeger()
        {
            this.InitializeComponent();

            qryDat = DBUtil.OpenSQL(@"
                DECLARE @OrgUnitID   int,
                        @TeamCode    int

                SET @OrgUnitID = {0}

                WHILE @OrgUnitID IS NOT NULL BEGIN
                  SELECT TOP 1 @TeamCode = GRP.Code
                  FROM   XLOVCode        TEM
                    INNER JOIN XLOVCode  GRP ON GRP.LOVName = 'QuOrgUnitGroup' AND ',' + TEM.Value3 + ',' LIKE '%,' + GRP.Value1 + ',%'
                  WHERE TEM.LOVName = 'QuOrgUnitTeam'
                    AND TEM.Code = @OrgUnitID
                  ORDER BY GRP.SortKey

                  IF @TeamCode IS NOT NULL BREAK

                  SELECT @OrgUnitID = ParentID FROM XOrgUnit WHERE OrgUnitID = @OrgUnitID
                  IF @@RowCount = 0 BREAK
                END

                SELECT TeamCode = IsNull(@TeamCode, -1), OrgUnitID = @OrgUnitID",
                Session.User["OrgUnitID"]);

                if (!DBUtil.UserHasRight("CtlQueryMigWVTraeger_AuswahlGruppe"))
                    edtSucheGruppe.EditMode = EditModeType.ReadOnly;

                if (!DBUtil.UserHasRight("CtlQueryMigWVTraeger_AuswahlTeam"))
                    edtSucheTeam.EditMode = EditModeType.ReadOnly;

            edtWVCode.SetLookupDataSource( DBUtil.OpenSQL(@"
                SELECT Code, Text = ShortText + IsNull( ' (' + Text + ')','')
                FROM XLOVCode
                WHERE LOVName = 'BaWVCode'
                ORDER BY SortKey"));

            edtBED.SetLookupDataSource( DBUtil.OpenSQL(@"
                SELECT Code, Text = CAST(Code as varchar) + IsNull( ' (' + Text + ')','')
                FROM XLOVCode
                WHERE LOVName = 'BaBED'
                ORDER BY SortKey"));

            edtWVCode.EditValue = "22,4,5,24,8,9";
            edtBED.EditValue = "16001,16002,16003,16004,16005,16006,16007,16008,16009,16010,16011,16012,16013,16014,16015,16016,16017,16018,16019,16020,16021,16022,16023,16024,16025,16029";
        }

