data.value:
DECLARE @OrgGruppeID INT;
SET @OrgGruppeID = NULL;
--- SET @OrgGruppeID = {ctlOrgUnitTeamUser.SucheGruppe.EditValue};

DECLARE @TeamID INT;
SET @TeamID = NULL;
--- SET @TeamID = {ctlOrgUnitTeamUser.SucheTeam.EditValue};

DECLARE @DatumEingangSDSVon DATETIME;
DECLARE @DatumEingangSDSBis DATETIME;
DECLARE @DatumAusgangSDSVon DATETIME; 
DECLARE @DatumAusgangSDSBis DATETIME;
DECLARE @InklErstellte      BIT;
--- SET @DatumEingangSDSVon = {edtEingangSDSVon.EditValue};
--- SET @DatumEingangSDSBis = {edtEingangSDSBis.EditValue};
--- SET @DatumAusgangSDSVon = {edtAusgangSDSVon.EditValue};
--- SET @DatumAusgangSDSBis = {edtAusgangSDSBis.EditValue};
--- SET @InklErstellte      = {edtInklErstellte.EditValue};

SELECT  [Person mit zivilr. Massnahme] = PRS.NameVorname,
        [Pers.-Nr]       = PRS.BaPersonID,
        Geburtstag       = PRS.Geburtsdatum,
        Todestag         = PRS.Sterbedatum,
        ZGB              = MAS.ZGB,
        [Behördliche Massnahme] = MAS.Mandatstyp,
        Berichtsart	     = BER.Berichtsart,
        [Bericht von]    = BER.DatumVon,
        [Bericht bis]    = BER.DatumBis,
        Fristerstreckung = BER.DatumFristerstreckung,
        [1. Mahnung]	   = BER.DatumMahnung1,
        [2. Mahnung]	   = BER.DatumMahnung2,
        [3. Mahnung]	   = BER.DatumMahnung3,
        Spesen           = BER.BetragSpesen,
        [Eingang SDS]    = BER.DatumEingangSDS,
        [Ausgang SDS]    = BER.DatumAusgangSDS,
        Erstellung       = BER.DatumBerichtMT,
        Tage             = DATEDIFF(d, ISNULL(BER.DatumBerichtMT, GETDATE()), BER.DatumBis),
        [Versand KESB]     = BER.DatumVersandAnVB,
        [Genehm. KESB]     = BER.DatumGenehmigung1,
        [Genehm. BRZ (gültig bis 31.12.2012)]    = BER.DatumGenehmigung2,
        [zust. MA]       = USR.NameVorname + ISNULL(' (' + USR.OrgUnit + ')', ''),
        [Beist. oder Vorm.]  = BER.MandatsTraegerName,
        [MA Klibu]		   = USRK.DisplayText,
        FallBaPersonID$  = FAL.BaPersonID,
        Historisiert     = CASE WHEN BER.IstAktiv = 1 THEN CONVERT(BIT, 0) ELSE CONVERT(BIT, 1) END,
        Abgeschlossen    = CONVERT(BIT, CASE WHEN LEI.DatumBis IS NULL THEN 0 ELSE 1 END)
FROM dbo.VmBericht           BER
  INNER JOIN dbo.VmMassnahme MAS  WITH (READUNCOMMITTED) ON MAS.VmMassnahmeID = BER.VmMassnahmeID
  INNER JOIN dbo.FaLeistung  LEI  WITH (READUNCOMMITTED) ON LEI.FaLeistungID = MAS.FaLeistungID
  INNER JOIN dbo.FaFall      FAL  WITH (READUNCOMMITTED) ON FAL.FaFallID = LEI.FaFallID
  INNER JOIN dbo.vwPerson    PRS  WITH (READUNCOMMITTED) ON PRS.BaPersonID = LEI.BaPersonID
  INNER JOIN dbo.vwUser      USR  WITH (READUNCOMMITTED) ON USR.UserID = LEI.UserID
  LEFT  JOIN dbo.vwUser      USRK WITH (READUNCOMMITTED) ON USRK.UserID = BER.MAKlibuUserID
WHERE (LEI.DatumBis IS NULL -- Nur Berichte aus aktiven Leistungen
--- OR {edtInklAbgeschlosseneLeistungen.EditValue} = 1
)
  AND ((@OrgGruppeID IS NULL AND @TeamID IS NULL) OR USR.OrgUnitID IN (SELECT OrgUnitID FROM dbo.fnOrgUnitsOfTeam(@OrgGruppeID,@TeamID)))
  AND (BER.DatumGenehmigung1 IS NULL OR @InklErstellte = 1)
  AND (BER.DatumBis > DATEADD(yyyy, -Convert(int, dbo.fnXConfig('System\Vormundschaft\Abfragen\Berichtsaufforderung\AlterMax', GETDATE())), GETDATE())
 OR BER.DatumBis IS NULL)
--- AND BER.DatumBis <= {edtFaelligBis} AND BER.DatumBerichtMT IS NULL
--- AND USR.UserID = {ctlOrgUnitTeamUser.SucheUserID.LookupID}
--- AND BER.DatumEingangSDS >= {edtEingangSDSVon}
--- AND BER.DatumEingangSDS <= {edtEingangSDSBis}
--- AND BER.DatumAusgangSDS >= {edtAusgangSDSVon}
--- AND BER.DatumAusgangSDS <= {edtAusgangSDSBis}
--- AND BER.MAKlibuUserID = {edtMAKlibu.LookupID}

--- AND ({edtInklHist.EditValue} = 1 OR BER.IstAktiv = 1) -- auch historisierte Berichte

ORDER BY [Bericht bis], [Person mit zivilr. Massnahme]
