        private void chkMAIstLeistungsverantwortlich_EditValueChanged(object sender, EventArgs e)
        {
            CheckboxenChanged();

            if (_MitarbeiterCheckboxUpdating) return;

            if (UtilsGui.IsCalledFromAnyMethod("OnSearch")) return;

            try
            {
                _MitarbeiterCheckboxUpdating = true;
                chkSachbearbeiter.Checked = !chkMAIstLeistungsverantwortlich.Checked;

                // Speichere das Layout, was wiederum die Methode OnGettingLayout() triggert, was dann den Checkbox-Status speichert
                SaveLayout();
            }
            finally
            {
                _MitarbeiterCheckboxUpdating = false;
            }
        }

