        private void qryQuery_AfterFill(object sender, System.EventArgs e)
        {
            DataSet ds = qryQuery.DataSet;

            ds.EnforceConstraints = false; // Workaround: das SqlQuery löscht die Constraints nur auf der zweiten Table, nicht jedoch auf der ersten
            ds.Relations.Clear();

            if (chkGruppiertNachFall.Checked)
            {
                try
                {
                    ds.Relations.Add("Leistungen",
                          new DataColumn[] { ds.Tables[0].Columns["FaFallID"] },
                          new DataColumn[] { ds.Tables[1].Columns["FaFallID"] });
                }
                catch (Exception ex)
                {
                    // Wir ignorieren diese Exception

                    // Eintrag ins Log.
                    LOG.ErrorFormat("Fehler in: [{0}]. Exception: [{1}]", GetType().FullName, ex.Message);

                    // Eintrag ins Log (Ansicht unter Anwendung/Newod Schnittstelle/Protokolle)
                    XLog.Create(GetType().FullName, LogLevel.ERROR, ex.Message);
                }
            }
        }

