        public override void OnSearch()
        {
            if (tabControlSearch.SelectedTab == tpgSuchen)
            {
                foreach (Control ctl in UtilsGui.AllControls(tpgSuchen))
                {
                    if (ctl is KissButtonEdit)
                    {
                        KissButtonEdit edt = (KissButtonEdit)ctl;
                        edt.CheckPendingSearchValue();
                    }

                    if (ctl is CtlOrgUnitTeamUser)
                    {
                        ((CtlOrgUnitTeamUser)ctl).CheckPendingSearchValue();
                    }
                }
            }

            // Workaround um F3 weiter zu reichen, da das FrameWork das OrgUnitTeamUser UserControl nicht kennt
            try
            {
                if (((ContainerControl)ActiveControl).ActiveControl.Equals(ctlOrgUnitTeamUser))
                    edtAufDatumNach.Focus();
            }
            catch
            {
            }

            base.OnSearch();

            RestoreLayout();		// Lade die Checkboxen neu, da base.OnSearch() die KiSS-Controls aus nicht ganz klaren Gründen jeweils neu initialisiert mit NULL-Werten

            // Prüfe, dass Checkboxen auch gesetzt wurden
            if (chkMAIstLeistungsverantwortlich.Checked == chkSachbearbeiter.Checked)
            {
                // Fehler: Entweder sind beide Checkboxen gesetzt oder beide sind nicht gesetzt => Setze nur LV
                chkMAIstLeistungsverantwortlich.Checked = true;
                chkSachbearbeiter.Checked = false;

                SaveLayout();
            }
        }

