        #region Protected Methods

        protected override void NewSearch()
        {
            base.NewSearch();
            ctlOrgUnitTeamUser.NewSearch();

            edtNurAktive.Checked = true;
            chkLeistungW.Checked = true;
            chkLeistungV.Checked = true;
            chkLeistungK.Checked = true;
            chkLeistungKVermittlung.Checked = true;

            chkNurSelektierteLeistungen.Checked = false;
            chkLeistungWIKV.Checked = false;
            chkLeistungWIKR.Checked = false;
            chkLeistungWIKU.Checked = false;

            chkGruppiertNachFall.Checked = true;

            edtAufDatumNach.Focus();
        }

        protected override void OnGettingLayout(KissLayoutEventArgs e)
        {
            base.OnGettingLayout(e);

            KissControlLayout.SaveSimpleProperty(e, chkMAIstLeistungsverantwortlich, "Checked");
            KissControlLayout.SaveSimpleProperty(e, chkSachbearbeiter, "Checked");
        }

        protected override void OnSettingLayout(KissLayoutEventArgs e)
        {
            base.OnSettingLayout(e);

            KissControlLayout.ReadSimpleProperty(e, chkMAIstLeistungsverantwortlich, "Checked");
            KissControlLayout.ReadSimpleProperty(e, chkSachbearbeiter, "Checked");
        }

        protected override void RunSearch()
        {
            ctlGotoFall.DataMember = "KlientensystemID$";
            ctlGotoFall.Visible = true;

            if (DBUtil.IsEmpty(ctlOrgUnitTeamUser.SucheUserID.EditValue))
            {
                KissMsg.ShowInfo("Sie müssen eine/n Mitarbeiter/in resp. eine/n Sachbearbeiter/in auswählen");
                throw new Exception();
            }

            if (!chkNurSelektierteLeistungen.Checked &&
                 !chkLeistungW.Checked &&
                 !chkLeistungV.Checked &&
                 !chkLeistungK.Checked &&
                 !chkLeistungKVermittlung.Checked &&
                 !chkLeistungWIKV.Checked &&
                 !chkLeistungWIKR.Checked &&
                 !chkLeistungWIKU.Checked &&
                 !chkLeistungF.Checked)
            {
                // Leistungen sind Oder-Verknüpft, d.h. mind. eine der Leistungen muss selektiert sein
                KissMsg.ShowInfo("Sie müssen mind. eine Leistung auswählen");
                throw new Exception();
            }

            base.RunSearch();
        }

