        #region Private Methods

        private void CheckboxenChanged()
        {
            // Setze die "und" resp. "oder" (resp. "mit" beim ersten selektierten Eintrag) der selektierten Leistungen
            string text = "";
            string selektiert = "";
            string nichtSelektiert = "";
            string groupboxText = "";
            string aktivInaktiv = " ";
            string MA = "";

            // Unterscheidung und / oder
            if (chkNurSelektierteLeistungen.Checked)
            {
                selektiert = "mit";
                text = "mit";
                nichtSelektiert = "ohne";
                groupboxText = " exakt ";
                lblLeistungF.Visible = false;
                chkLeistungF.Visible = false;
            }
            else
            {
                selektiert = "oder";
                groupboxText = " mind. ";
                lblLeistungF.Visible = true;
                chkLeistungF.Visible = true;
            }

            if (chkMAIstLeistungsverantwortlich.Checked)
            {
                MA = " (MA = LV)";
            }
            else
            {
                MA = " (MA = SB)";
            }

            // Unterscheidung Aktive / Inaktive Leistungen
            if (edtNurAktive.Checked)
            {
                aktivInaktiv = " aktiven ";
            }
            else if (edtNurPassive.Checked)
            {
                aktivInaktiv = " inaktiven ";
            }

            // Setze Caption der Checkboxen
            if (chkLeistungF.Checked && chkLeistungF.Visible) { lblLeistungF.Text = text; text = selektiert; } else { lblLeistungF.Text = nichtSelektiert; }
            if (chkLeistungW.Checked) { lblLeistungW.Text = text; text = selektiert; } else { lblLeistungW.Text = nichtSelektiert; }
            if (chkLeistungV.Checked) { lblLeistungV.Text = text; text = selektiert; } else { lblLeistungV.Text = nichtSelektiert; }
            if (chkLeistungK.Checked) { lblLeistungK.Text = text; text = selektiert; } else { lblLeistungK.Text = nichtSelektiert; }
            if (chkLeistungKVermittlung.Checked) { lblLeistungKVermittlung.Text = text; text = selektiert; } else { lblLeistungKVermittlung.Text = nichtSelektiert; }

            // Setze die Captions der Groupboxen
            grpLeistungen.Text = "mit" + groupboxText + "folgenden" + aktivInaktiv + "Leistungen" + MA;
            grpLeistungenWIK.Text = "oder mit mind. folgenden" + aktivInaktiv + "WIK-Leistungen" + MA;
        }

        private void CheckboxenWIKChanged()
        {
            string selektiert = "";

            if (chkLeistungWIKV.Checked) { lblLeistungWIKV.Text = selektiert; selektiert = "oder"; } else { lblLeistungWIKV.Text = ""; }
            if (chkLeistungWIKR.Checked) { lblLeistungWIKR.Text = selektiert; selektiert = "oder"; } else { lblLeistungWIKR.Text = ""; }
            if (chkLeistungWIKU.Checked) { lblLeistungWIKU.Text = selektiert; selektiert = "oder"; } else { lblLeistungWIKU.Text = ""; }
        }

