        private void edtNurAktive_EditValueChanged(object sender, System.EventArgs e)
        {
            if (edtNurAktive.Checked)
            {
                edtNurPassive.Checked = false;
            }
        }

        private void edtNurPassive_EditValueChanged(object sender, System.EventArgs e)
        {
            if (edtNurPassive.Checked)
            {
                edtNurAktive.Checked = false;
            }
        }

        private void edtSucheOE_EditValueChanged(object sender, System.EventArgs e)
        {
            //edtSucheTeam.LOVFilter = "FF" + DBUtil.ExecuteScalarSQL(@"
            edtSucheTeam.LOVFilter = (string)DBUtil.ExecuteScalarSQL(@"
            --         SELECT Filter = IsNull(','+ Value1, '')
                     SELECT Filter = IsNull(Value1, '')
                     FROM XLOVCode
                     WHERE LOVName = 'QuOrgUnitGroup' AND Code = {0}
                     UNION ALL SELECT 'FF'", edtSucheOE.EditValue);
            edtSucheTeam.LOVName = "QuOrgUnitTeam";
        }

        private void edtUserID_UserModifiedFld(object sender, KiSS4.Gui.UserModifiedFldEventArgs e)
        {
            string SearchString = edtUserID.Text.Replace("*", "%").Replace("?", "_").Replace(" ", "%");

            KissLookupDialog dlg = new KissLookupDialog();
            if (e.ButtonClicked || !DBUtil.IsEmpty(SearchString))
            {
                if (DBUtil.IsEmpty(SearchString))
                    SearchString = "%";

                e.Cancel = !dlg.SearchData(@"
                    SELECT
                      ID$              = UserID,
                      UserID$          = UserID,
                      DisplayText$     = DisplayText,
                      OrgUnit$         = OrgUnit,
                      [Kürzel]         = LogonName,
                      [Mitarbeiter/in] = NameVorname,
                      [Org. Einheit]   = OrgUnit
                    FROM vwUser
                    WHERE DisplayText LIKE '%' + {0} + '%'
                    ORDER BY NameVorname
                    ", SearchString, e.ButtonClicked);

                if (e.Cancel) return;
            }

            edtUserID.EditValue = dlg["DisplayText$"];
            edtUserID.LookupID = dlg["ID$"];
        }

