        #region Private Methods

        private void btnAdd_Click(object sender, System.EventArgs e)
        {
            if (qryQuery.Count == 0) return;
            foreach (System.Data.DataRow row in qryQuery.DataTable.Rows) row["Selektion"] = true;
        }

        private void btnAusgleich_Click(object sender, System.EventArgs e)
        {
            qryQuery.Post();

            int Anzahl = 0;
            foreach (DataRow row in qryQuery.DataTable.Rows)
            {
                if (Utils.ConvertToBool(row["Selektion"], false)) Anzahl += 1;
            }
            if (Anzahl == 0)
            {
                KissMsg.ShowInfo("Es sind keine Zeilen gewählt.");
                return;
            }

            int IkAlvAusgleichbufferID = 0;
            int AnzUnbek = 0;
            int AnzOKs = 0;

            foreach (DataRow row in qryQuery.DataTable.Rows)
            if (Utils.ConvertToBool(row["Selektion"], false))
            {
                try
                {
                    DBUtil.ExecSQLThrowingException(
                        "exec dbo.spPscdProcessAlvAusgleichBuffer {0}, {1}",
                        row["IkAlvAusgleichBufferID"],
                        Session.User.UserID
                    );
                    AnzOKs += 1;
                }
                catch (Exception ex)
                {
                    IkAlvAusgleichbufferID = Utils.ConvertToInt(row["IkAlvAusgleichBufferID"]);
                    AnzUnbek += 1;
                    DBUtil.ExecSQL(
                        "update dbo.IkAlvAusgleichBuffer set Fehlermeldung = {0} where IkAlvAusgleichBufferID = {1}",
                        ex.Message,
                        row["IkAlvAusgleichBufferID"]
                    );
                }
            }

            string MsgText = "";
            if (AnzUnbek == 1)
            {
                MsgText = "Bei der Verarbeitung ist 1 Fehler aufgetreten.";
            }
            else if (AnzUnbek > 0)
            {
                MsgText = string.Format(
                  "Bei der Verarbeitung sind {0} Fehler aufgetreten.",
                  AnzUnbek.ToString()
                );
            }
            if (MsgText != "") MsgText += "\r\n";
            if (AnzOKs == 1)
            {
                MsgText = "1 gewählte Zeile konnte erfolgreich verarbeitet werden.";
            }
            else if (AnzOKs > 0)
            {
                MsgText = string.Format(
                  "{0} gewählte Zeilen konnten erfolgreich verarbeitet werden.",
                  AnzOKs.ToString()
                );
            }
            KissMsg.ShowInfo(MsgText);

            // Daten neu anzeigen
            qryQuery.Fill();

            // Zum letzten Fehler springen
            if (IkAlvAusgleichbufferID > 0) qryQuery.Find("IkAlvAusgleichbufferID=" + IkAlvAusgleichbufferID.ToString());
        }

        private void btnMinus_Click(object sender, System.EventArgs e)
        {
            if (qryQuery.Count == 0) return;
            foreach (System.Data.DataRow row in qryQuery.DataTable.Rows) row["Selektion"] = false;
        }

        private void gvwAusgleich_CustomDrawCell(object sender, DevExpress.XtraGrid.Views.Base.RowCellCustomDrawEventArgs e)
        {
            // 05.05.2008 : sozheo : Korrekturen für neues Feld "IkGlaeubigerStatusCode"
            // 27.02.2009 : sozheo : Korrekturen für neue Tabelle IkInterneVerrechnung

            if (e.Column.FieldName != "Selektion" && e.RowHandle != gvwAusgleich.FocusedRowHandle)
            {
                // Wenn nicht Intern und Zahlungsweg nicht definiert ist,
                // dann soll Zeile rot sein
                if (
                    !Utils.ConvertToBool(gvwAusgleich.GetRowCellValue(e.RowHandle, colIntVerrechnung), true) &&
                    !Utils.ConvertToBool(gvwAusgleich.GetRowCellValue(e.RowHandle, colZW), true)
                )
                    e.Appearance.BackColor = Color.LightSalmon;
                else
                    e.Appearance.BackColor = Color.BlanchedAlmond;
            }
        }

        private void qryQuery_AfterFill(object sender, System.EventArgs e)
        {
            btnAusgleich.Enabled = (qryQuery.Count > 0);
        }

