        // -------------------------------------------------------------
        private void EineBuchungStornieren(object BuchungID)
        {
            // Kontrolle an PSCD, ob storniert werden darf
            int[] iArray = new int[1];
            string buchungIDs = "";

            iArray[0] = Utils.ConvertToInt(BuchungID);
            buchungIDs = (buchungIDs == "") ? iArray[0].ToString() : buchungIDs = ", " + iArray[0].ToString();

            try
            {
              	// PSCD Storno

                Session.BeginTransaction();
              	try
              	{
                    // Zuerst erstellen wir neue Storno-Einträge in der Tabelle KbBuchungStorno

                    // Alle selektierten in der Storno-Tabelle zufügen (falls sie nicht schon existieren)
                    DB.DBUtil.ExecSQLThrowingException(@"
                    INSERT INTO dbo.KbBuchungStorno
                    (KbBuchungID, StornierungVermerktUserID, StornierungVermerktDatum)
                    (
                        SELECT BUC.KbBuchungID, {0}, {1} FROM dbo.KbBuchung BUC
                            LEFT JOIN dbo.KbBuchungStorno STO ON STO.KbBuchungID = BUC.KbBuchungID
                        WHERE BUC.KbBuchungID IN (" + buchungIDs + @")
                                AND STO.KbBuchungID IS NULL		-- Es gibt noch keine Storno-Eintrag für diesen Beleg
                    )
                    ", Session.User.UserID, DateTime.Now);

                    Session.Commit();
                }
                catch(Exception ex2)
                {
                    if (Session.Transaction != null)
                    {
                     	Session.Rollback();
                    }

                    KissMsg.ShowError("Fehler beim Speichern der Stornovorgänge in der Kiss-DB. Der Storno-Vorgang wurde nicht ausgeführt. Fehlermeldung: " + ex2.Message);
                    return;
                }

                // StornoDatum = Tagesdatum
              	PSCDInterface.StornoNettoBelege(iArray, DateTime.Today);
            }
            catch (Exception ex)
            {
                KissMsg.ShowInfo(ex.Message);
                return;
            }
        }

        // Letzte Bearbeitung
        // 24.04.2009 : sozheo : neu
        // -------------------------------------------------------------
        private void EinenFehlerBeheben(object Status, object Beleg, object BuchungID)
        {
            if (qryQuery.Count == 0) return;

              if (Utils.ConvertToInt(edtFehler.EditValue) == 1)
              {
            // Forderungen bei Uebh und KKBB-Nachzahlungen löschen
            // -----------------------------------------------------
            if (Utils.ConvertToInt(Status) == 2 && DBUtil.IsEmpty(Beleg))
            {
              // Zeile noch nicht ans PSCD gesendet, also Buchung löschen
              DBUtil.ExecSQL("Delete dbo.KbBuchung where KbBuchungID = {0}", BuchungID);
            }
            else
            if (Utils.ConvertToInt(Status) == 3)
            {
              // Zeile bereits ans PSCD gesendet, also stornieren
              EineBuchungStornieren(BuchungID);
            }
              }
        }

        private void btnEinen_Click(object sender, System.EventArgs e)
        {
            // Letzte Bearbeitung
            // 24.04.2009 : sozheo : neu
            // -------------------------------------------------------------

            if (!KissMsg.ShowQuestion("Wollen Sie diese Buchung wirklich löschen/stornieren?")) return;

            EinenFehlerBeheben(qryQuery["Status"], qryQuery["Beleg"], qryQuery["KbBuchungID"]);

            qryQuery.Refresh();
        }

        private void btnKorrektur_Click(object sender, System.EventArgs e)
        {
            // Letzte Bearbeitung
            // 24.04.2009 : sozheo : neu
            // -------------------------------------------------------------

            if (!KissMsg.ShowQuestion("Wollen Sie alle Buchungen wirklich löschen/stornieren?")) return;

            foreach (System.Data.DataRow row in qryQuery.DataTable.Rows)
            {
              EinenFehlerBeheben(row["Status"], row["Beleg"], row["KbBuchungID"]);
            }

            qryQuery.Refresh();
        }

        private void qryQuery_PositionChanged(object sender, System.EventArgs e)
        {
            // Letzte Bearbeitung
            // 24.04.2009 : sozheo : neu
            // -------------------------------------------------------------

            btnKorrektur.Enabled = (qryQuery.Count > 0);
        }

        #endregion
