        private void tabControlSearch_SelectedTabChanging(object sender, System.ComponentModel.CancelEventArgs e)
        {
            // Letzte Bearbeitung
            // 14.02.2008 : sozheo : Neu
            // 18.08.2008 : sozheo : Alles kopiert von CtlIkSollstellung
            // ------------------------------------------------------------------------
            e.Cancel = false;
            if (IsCreating) return;

            if (tabControlSearch.SelectedTabIndex == 0) return;

            if (DBUtil.IsEmpty(edtSollstellungsMonat.EditValue)) 
            {
                KissMsg.ShowInfo("Wählen Sie zuerst den Verarbeitungsmonat.");
                e.Cancel = true;
            }

            try
            {
                if (!DBUtil.IsEmpty(edtFallNr.EditValue)) 
                {
                    int FNr = Convert.ToInt32(Utils.ConvertToString(edtFallNr.EditValue));
                }
            }
            catch (Exception ex)
            {
                // Eintrag ins Log.
                LOG.ErrorFormat("Fehler in: [{0}]. Exception: [{1}]", GetType().FullName, ex.Message);

                // Eintrag ins Log (Ansicht unter Anwendung/Newod Schnittstelle/Protokolle)
                XLog.Create(GetType().FullName, LogLevel.ERROR, ex.Message);

                KissMsg.ShowInfo("Die Fallnummer darf nur Zahlen enthalten.");
                e.Cancel = true;
            }
        }

