        private void btnSync_Click(object sender, System.EventArgs e)
        {
            if (KissMsg.ShowQuestion("Wollen Sie alle M-Leistungen im KiSS mit Daten aus VIS aktualisieren?\r\nZur Information: Dies kann u.U. mehrere Minuten gehen."))
            {
                // Synchroniziere mit VIS, und zwar alle Leistungen
                UtilsVIS.SyncVISToKiSS();
            }
        }

        private void CtlQueryIkVormundMassnahmen_Load(object sender, System.EventArgs e)
        {
            edtUser.EditValue = DBUtil.ExecuteScalarSQL("select DisplayText from vwUser where UserID = {0}", Session.User.UserID);
            edtUser.ToolTip = (string)edtUser.EditValue;
            edtUser.LookupID = Session.User.UserID;

            CheckUserModified();
        }

        private void edtUser_EditValueChanged(object sender, System.EventArgs e)
        {
            _sucheUserIDEditValueChanged = true;
        }

        private void edtUser_Enter(object sender, System.EventArgs e)
        {
            _sucheUserIDEditValueChanged = false;
        }

        private void edtUser_Leave(object sender, System.EventArgs e)
        {
            if (_sucheUserIDEditValueChanged)
            {
                CheckUserModified();
            }
        }

        private void edtUser_UserModifiedFld(object sender, KiSS4.Gui.UserModifiedFldEventArgs e)
        {
            e.Cancel = ValidateUser(e.ButtonClicked);
        }

        private bool ValidateUser(bool buttonClicked)
        {
            // Letzte Bearbeitung
            // 28.04.2009 : nweber : neu
            // -------------------------------------------------------------
            bool cancel = false;

            string SearchString = edtUser.Text.Replace("*", "%").Replace("?", "_").Replace(" ", "%");

            if (DBUtil.IsEmpty(SearchString))
            {
                if (buttonClicked)
                {
                    SearchString = "";
                }
                else
                {
                    edtUser.EditValue = "";
                    edtUser.ToolTip = "";
                    edtUser.LookupID = null;
                    return false;
                }
            }

            KissLookupDialog dlg = new KissLookupDialog();
            cancel = !dlg.SearchData(@"
              SELECT UserID$ = UserID,
                DisplayText$ = DisplayText,
                [Kürzel] = LogonName,
                [Mitarbeiter/in] = NameVorname,
                [Org.Einheit] = OrgUnit,
                LogonNameVornameOrgUnit$ = LogonNameVornameOrgUnit
              FROM   dbo.vwUser
              WHERE ( DisplayText LIKE '%' + {0} + '%' or  {0} IS NULL)
                AND OrgUnitID IN (select OrgUnitID from XOrgUnit where UserProfileCode = 7)
              ORDER BY NameVorname",
              SearchString,
              buttonClicked, null, null, null);

            if (!cancel)
            {
                edtUser.EditValue = Utils.ConvertToString(dlg[1]);
                edtUser.ToolTip = Utils.ConvertToString(dlg[1]);
                edtUser.LookupID = Utils.ConvertToInt(dlg[0]);
            }

            CheckUserModified();

            return cancel;
        }

