            // Alte Suchwerte speichern
              edtSaldoPer.EditValue = Old_SaldoPer;
              edtModul.EditValue = Old_Modul;
              edtKontrolleFuer.EditValue = Old_KontrolleFuer;
        }

        protected override void RunSearch()
        {
            // Alte Suchwerte speichern
              Old_SaldoPer = edtSaldoPer.DateTime;
              Old_Modul = Utils.ConvertToInt(edtModul.EditValue);
              Old_KontrolleFuer = Utils.ConvertToInt(edtKontrolleFuer.EditValue);
        }

