        private void CtlQueryIkZahlungskontrolle_Load(object sender, System.EventArgs e)
        {
            // Letzte Bearbeitung
            // 25.08.2008 : sozheo : Neu
            // -------------------------------------------------------------

            // Starteinstellungen seten
            Old_SaldoPer = DateTime.Today;
            Old_Modul = 4;
            Old_KontrolleFuer = 1;

            // Beim Start soll auf die Suche fokussiert werden
            tabControlSearch.SelectedTabIndex = 1;
            NewSearch();
        }

        private void qryQuery_AfterFill(object sender, System.EventArgs e)
        {
            // Letzte Bearbeitung
            // 25.08.2008 : sozheo : Neu
            // -------------------------------------------------------------

            foreach (System.Data.DataRow row in qryQuery.DataTable.Rows)
            {
              row["Saldo"] =
                Utils.ConvertToDecimal(row["Forderungen"]) -
                Utils.ConvertToDecimal(row["Zahlungen"]);
            }
        }

