        #region EventHandlers

        private void edtErstelltBisX_EditValueChanged(object sender, EventArgs e)
        {
            if (!DBUtil.IsEmpty(edtErstelltBisX.EditValue))
            {
                int daysInMonth = DateTime.DaysInMonth(edtErstelltBisX.DateTime.Year, edtErstelltBisX.DateTime.Month);
                if(edtErstelltBisX.DateTime.Day != daysInMonth)
                    edtErstelltBisX.DateTime = new DateTime(edtErstelltBisX.DateTime.Year, edtErstelltBisX.DateTime.Month, daysInMonth);
                //if (!DBUtil.IsEmpty(edtErstelltVonX.EditValue) && edtErstelltBisX.DateTime < edtErstelltVonX.DateTime)
                //{
                //    edtErstelltVonX.DateTime = new DateTime(edtErstelltBisX.DateTime.Year, edtErstelltBisX.DateTime.Month, 1);
                //}
            }
        }

        private void edtErstelltVonX_EditValueChanged(object sender, EventArgs e)
        {
            if (!DBUtil.IsEmpty(edtErstelltVonX.EditValue))
            {
                if(edtErstelltVonX.DateTime.Day != 1)
                    edtErstelltVonX.DateTime = new DateTime(edtErstelltVonX.DateTime.Year, edtErstelltVonX.DateTime.Month, 1);
                //if (!DBUtil.IsEmpty(edtErstelltBisX.EditValue) && edtErstelltBisX.DateTime < edtErstelltVonX.DateTime)
                //{
                //    int daysInMonth = DateTime.DaysInMonth(edtErstelltVonX.DateTime.Year, edtErstelltVonX.DateTime.Month);
                //    edtErstelltBisX.DateTime = new DateTime(edtErstelltVonX.DateTime.Year, edtErstelltVonX.DateTime.Month, daysInMonth);
                //}
            }
        }

        private void tabControlSearch_SelectedTabChanged(SharpLibrary.WinControls.TabPageEx page)
        {
            lblSucheNach.Text = "";
            if (page == tpgListe)
            {
                if (!DBUtil.IsEmpty(edtErstelltVonX.Text))
                    lblSucheNach.Text += "von " + edtErstelltVonX.Text;
                if (!DBUtil.IsEmpty(edtErstelltBisX.Text))
                    lblSucheNach.Text += " bis " + edtErstelltBisX.Text;
            }
        }

