/* Description 9507 - Gibt eine Tabelle der Fälle die während 4 Monaten keine Geld bekommen haben. Zeitbereich bis 12 Monaten. */
DECLARE @TeamID INT; --- SET @TeamID = {edtSucheTeam};
DECLARE @UserID INT; --- SET @UserID = {edtSucheUserID.LookupID};
DECLARE @Abfragedatum DATETIME; SET @Abfragedatum = dbo.fnDateOf(GETDATE());
-- -- PeriodMitBuch. PeriodeOhneBuch. -- >0 =0 -- |----------------|------| -- -12M -4M heute -- DECLARE @DatumVonPeriodeOhneBuchung DATETIME; DECLARE @DatumBisPeriodeOhneBuchung DATETIME; DECLARE @DatumVonPeriodeMitBuchung DATETIME; DECLARE @DatumBisPeriodeMitBuchung DATETIME;
SET @DatumVonPeriodeOhneBuchung = DATEADD(MONTH, -4, dbo.fnDateSerial(YEAR(@Abfragedatum),MONTH(@Abfragedatum),1)) SET @DatumBisPeriodeOhneBuchung = @Abfragedatum SET @DatumVonPeriodeMitBuchung = DATEADD(MONTH, -12, dbo.fnDateSerial(YEAR(@Abfragedatum),MONTH(@Abfragedatum),1)) SET @DatumBisPeriodeMitBuchung = @DatumVonPeriodeOhneBuchung
DECLARE @RelevanteBuchungen TABLE ( BaPersonID INT, FaLeistungID INT, AusgleichsDatum DATETIME
)
-- Get Max AusgleichtDatum von RelevanteBuchungen -- Hier müssen wir für jede BaPerson die FaLeistung der neuste Ausgleichdatum anzeigen.
;WITH RelevanteBuchungenCte(BaPersonID, FaLeistungID, AusgleichsDatum) AS -- Liste der RelevanteBuchungen ( SELECT BaPersonID, FaLeistungID, Ausgleichsdatum FROM fnGetRelevanteBuchungen(@DatumVonPeriodeMitBuchung, @DatumBisPeriodeOhneBuchung, @UserID, NULL,NULL,@TeamID) ), RelevanteBaPersonCte(BaPersonID, Ausgleichdatum) AS -- Max(AusgleichDatum) Group By BaPersonID ( SELECT BaPersonID, AusgleichsDatum = MAX(AusgleichsDatum) FROM RelevanteBuchungenCte GROUP BY BaPersonID ), RelevanteBuchungenCteNurMaxAusgleichDatum (BaPersonID, FaLeistungID, Ausgleichsdatum) AS -- Max(FaLeistungID) für diese AusgleichDatum, Group by BaPersonID ( SELECT BaPersonID = RelevanteBuchungenCte.BaPersonID, FaLeistungID= MAX(RelevanteBuchungenCte.FaLeistungID), AusgleichsDatum = MAX(RelevanteBaPersonCte.Ausgleichdatum) -- AusgleichtDatum schon auf Max, hier aber wegen der Struktur des Queries auch nötig FROM RelevanteBuchungenCte JOIN RelevanteBaPersonCte ON RelevanteBuchungenCte.BaPersonID = RelevanteBaPersonCte.BaPersonID AND RelevanteBuchungenCte.AusgleichsDatum = RelevanteBaPersonCte.Ausgleichdatum GROUP BY RelevanteBuchungenCte.BaPersonID )
/* Muster mit einfachen Werten
;WITH A (BaPersonID, FaLeistungID, Ausgleichdatum ) AS ( SELECT BaPersonID = 1, FaLeistungID = 5, Ausgleichdatum = '2014.08.30' UNION ALL SELECT BaPersonID = 1, FaLeistungID = 6, Ausgleichdatum = '2014.08.15' UNION ALL SELECT BaPersonID = 1, FaLeistungID = 7, Ausgleichdatum = '2014.08.01' UNION ALL SELECT BaPersonID = 2, FaLeistungID = 5, Ausgleichdatum = '2014.08.20' UNION ALL SELECT BaPersonID = 2, FaLeistungID = 6, Ausgleichdatum = '2014.08.20' UNION ALL SELECT BaPersonID = 2, FaLeistungID = 7, Ausgleichdatum = '2014.08.10' ), At (BaPersonID, Ausgleichdatum) As ( SELECT BaPersonID, Ausgleichdatum = MAX(Ausgleichdatum) FROM A GROUP BY BaPersonID ), Att (BaPersonID, FaLeistungID, AusgleichDatum) As ( SELECT BaPersonID = A.BaPersonID, FaLeistungID= MAX(A.FaLeistungID), AusgleichDatum = MAX(At.AusgleichDatum) FROM A JOIN At ON A.BaPersonID = At.BaPersonID AND A.AusgleichDatum = At.AusgleichDatum GROUP BY A.BaPersonID )
SELECT * FROM Att */
SELECT FallBaPersonID$ = FAL.BaPersonID, Abfragedatum = @Abfragedatum, [Monat mit letzter SH-Zahlung] = dbo.fnDateAsVarchar(AusgleichsDatum, 'yyyy.mm'), Quartierteam = USR.OrgUnit, [Fallführung W] = USR.LogonName, [Fall-Nr.] = FAL.FaFallID, [LE-NR.] = LEI.FaLeistungID, [Pers-Nr. Leistungsträger] = PRS.BaPersonID, [Nachname Leistungsträger] = PRS.Name, [Vorname Leistungsträger] = PRS.Vorname, Abschlussgrund = dbo.fnLOVText('AbschlussGrund', LEI.AbschlussGrundCode), [Datum bis] = LEI.DatumBis FROM RelevanteBuchungenCteNurMaxAusgleichDatum rlb JOIN FaLeistung LEI ON rlb.FaLeistungID = LEI.FaLeistungID INNER JOIN FaFall FAL WITH(READUNCOMMITTED) ON LEI.FaFallID = FAL.FaFallID INNER JOIN vwPerson PRS ON PRS.BaPersonID = LEI.BaPersonID LEFT JOIN vwUser USR ON USR.UserID = FAL.UserID WHERE 1=1 AND rlb.AusgleichsDatum < @DatumVonPeriodeOhneBuchung -- Neuste Buchung muss vor dem Zeitbereich 'OhneBuchung' stehen AND USR.UserID = ISNULL(@UserID, USR.UserID) AND USR.OrgUnitID = ISNULL(@TeamID, USR.OrgUnitID) ORDER BY AusgleichsDatum DESC, Quartierteam, USR.LogonName
