                //}
            }
        }

        private void tabControlSearch_SelectedTabChanged(SharpLibrary.WinControls.TabPageEx page)
        {
            lblSucheNach.Text = "";
            if (page == tpgListe)
            {
                if (!DBUtil.IsEmpty(edtErstelltVonX.Text))
                    lblSucheNach.Text += "von " + edtErstelltVonX.Text;
                if (!DBUtil.IsEmpty(edtErstelltBisX.Text))
                    lblSucheNach.Text += " bis " + edtErstelltBisX.Text;
            }
        }

