        #region Public Methods

        public override void OnSearch()
        {
            // Workaround um F3 weiter zu reichen, da das FrameWork das OrgUnitTeamUser UserControl nicht kennt
            try
            {
                if (((ContainerControl)ActiveControl).ActiveControl.Equals(ctlOrgUnitTeamUser))
                    edtWhSucheKlientX.Focus();
            }
            catch
            {
            }

            base.OnSearch();
        }

        #endregion

        #region Protected Methods

        // Begin Rule
        // RuleName: NewSearch
        protected override void NewSearch()
        {
            //edtErstelltVonX.EditValue = new DateTime(DateTime.Now.Year, 1, 1);
            //edtErstelltBisX.EditValue = new DateTime(DateTime.Now.Year, 12, 31);
            base.NewSearch();
            ctlOrgUnitTeamUser.NewSearch();
        }

        // Begin Rule
        // RuleName: RunSearch
        protected override void RunSearch()
        {
            if (DBUtil.IsEmpty(edtErstelltVonX.EditValue) || DBUtil.IsEmpty(edtErstelltBisX.EditValue))
            {
                KissMsg.ShowInfo("Das Feld 'Datum von' und 'Datum bis' muss ausgefüllt werden");
                throw new Exception();
            }

            if (
                DBUtil.IsEmpty(ctlOrgUnitTeamUser.SucheTeam.EditValue) &&
                DBUtil.IsEmpty(ctlOrgUnitTeamUser.SucheUserID.EditValue) &&
                DBUtil.IsEmpty(edtWhSucheKlientX.EditValue) &&
                DBUtil.IsEmpty(this.edtPersonenNr.EditValue) &&
                DBUtil.IsEmpty(edtFaFallID.EditValue)
                )
            {
                KissMsg.ShowInfo("Mindestens eines der folgenden Felder muss ausgefüllt werden:\r\n\r\n" +
                    //                    "- Organisationsgruppe\r\n" +
                    "- Team\r\n" +
                    "- Mitarbeitende\r\n" +
                    "- Personen-Name\r\n" +
                    "- Personen-Nummer\r\n" +
                    "- Fall-Nr");
                throw new Exception();
            }

            if ((DateTime)edtErstelltVonX.EditValue > (DateTime)edtErstelltBisX.EditValue)
            {
                KissMsg.ShowInfo("Das Feld 'Datum von' muss ein früheres Datum als das Feld 'Datum bis' enthalten.");
                throw new Exception();
            }

            base.RunSearch();
        }

        #endregion

        private void qryQuery_AfterFill(object sender, EventArgs e)
        {
            // Keine Person ausgewählt => Gruppierung
            DataSet ds = qryQuery.DataSet;
            ds.EnforceConstraints = false; // Workaround: das SqlQuery löscht die Constraints nur auf der zweiten Table, nicht jedoch auf der ersten
            // Beim ersten Durchgang ohne die Option 'verdichtet' (mit vorgängigen Suchvorgängen mit verdichtet) gibt es dadurch eine exception.
            try
            {
                ds.Relations.Add("lvlBelege",
                    new DataColumn[] { ds.Tables[0].Columns["FaFallID"] },
                    new DataColumn[] { ds.Tables[1].Columns["FaFallID"] });
            }
            catch (Exception ex)
            {
                // was tun? ignorieren?

                // Eintrag ins Log.
                LOG.ErrorFormat("Fehler in: [{0}]. Exception: [{1}]", GetType().FullName, ex.Message);

                // Eintrag ins Log (Ansicht unter Anwendung/Newod Schnittstelle/Protokolle)
                XLog.Create(GetType().FullName, LogLevel.ERROR, ex.Message);
            }

        }

        #endregion

