        #region Private Methods

        private void CtlQueryWhZahllaufJournal_Load(object sender, System.EventArgs e)
        {
            edtModul.LoadQuery(DBUtil.OpenSQL(@"
                select Code = ModulID, Text = ShortName + ' - ' + Name
                from XModul
                where ModulID in (3,4)
                union all
                select null,''
                order by Code"));

            edtSucheZahlungForderung.LoadQuery(DBUtil.OpenSQL(@"
                select Code = convert(int,null), Text = ''
                union all
                select Code = 1, Text = 'Zahlung an Kreditor'
                union all
                select Code = 2, Text = 'Forderung an Debitor'"));
        }

