        private string ComposeLAList(SqlQuery qry)
        {
            string LAList = "";
            foreach (DataRow row in qry.DataTable.Rows)
            {
                if (row.RowState != DataRowState.Deleted)
                {
                    if (LAList != "") LAList += ",";
                    LAList += row["KontoNr"].ToString();
                }
            }
            return LAList;
        }
