        #region Constructors

        public CtlBgGrundbedarf(Image titelImage, string titelText, int bgBudgetID)
            : this()
        {
            this.picTitle.Image = titelImage;
            this.BgBudgetID = bgBudgetID;
            this.BgFinanzplanID = (int)DBUtil.ExecuteScalarSQL("SELECT BgFinanzplanID FROM BgBudget WHERE BgBudgetID = {0}", this.BgBudgetID);

            //recalculate GBI,GBII ...
            try  //TODO
            {
                DBUtil.ExecSQLThrowingException("EXECUTE spWhFinanzPlan_Update {0}", BgFinanzplanID);
            }
            catch{}

            ShUtil.ApplyShStatusCodeToSqlQuery(this.ActiveSQLQuery, BgBudgetID);

            SqlQuery qry = DBUtil.OpenSQL(@"
                SELECT BBG.BgFinanzplanID,
                  FinanzPlanVon = IsNull(SFP.DatumVon, SFP.GeplantVon),
                  FinanzPlanBis = IsNull(SFP.DatumBis, SFP.GeplantBis)
                FROM BgBudget              BBG
                  INNER JOIN BgFinanzplan  SFP ON SFP.BgFinanzplanID = BBG.BgFinanzplanID
                WHERE BBG.BgBudgetID = {0}",
                BgBudgetID);
            this.lblTitel.Text = string.Format(this.lblTitel.Text, qry["FinanzPlanVon"], qry["FinanzPlanBis"], titelText);

            qryKennzahlen.Fill(qry["BgFinanzplanID"]);

            qryBgPosition.Fill(qryBgPosition.SelectStatement, BgBudgetID, this.edtBgPositionsartID.LOVName);

            // Reallign das Begründungs-Panel. Dies ist im Designer extra auf der rechten Seite, damit man die beiden SKOS-Panels besser bearbeiten kann
            int width = pnlBegruendung.Right;
            pnlBegruendung.Left = 0;
            pnlBegruendung.Width = width;
        }

