        private void btnDrucken_Click(object sender, System.EventArgs e)
        {
            try
            {
                DBUtil.ThrowExceptionOnOpenTransaction();
            }
            catch (Exception ex)
            {
                KissMsg.ShowError(ex.Message);
                return;
            }

            if (qryKbBuchung.Count == 0) return;

            if (!qryKbBuchung.CanUpdate)
            {
                KissMsg.ShowInfo("Für das Drucken des Barbelegs ist das Mutations-Recht für diese Maske erforderlich!\r\n\r\n" +
                                 "(Das Druckdatum und die auslösende Mitarbeiter/-in werden im Beleg vermerkt\r\n" +
                                 "und der Status wird eventuell auf 'gedruckt' gesetzt.)");
                return;
            }

            if ((int)qryKbBuchung["KbBuchungStatusCode"] == 2)  //grün -> gedruckt
            {
                if (!KissMsg.ShowQuestion("Achtung:\r\n" +
                                          "Durch den Druck des Barbelegs wird der Belegstatus auf 'ausgedruckt' gesetzt.\r\n" +
                                          "Dieser Vorgang kann nicht mehr rückgängig gemacht werden und " +
                                          "das Monatsbudget kann danach nicht mehr auf grau gestellt werden.\r\n\r\n" +
                                          "Der Barbeleg kann bei Druckerproblemen nochmals ausgedruckt werden.\r\n\r\n" +
                                          "Soll der Barbeleg gedruckt werden?\r\n"))
                    return;
            }

            // #5612: Limitierung des Barbezugs
            decimal maxBarBetrag = (decimal)DBUtil.GetConfigValue(@"System\Sozialhilfe\MaxBarBezugBetrag", 0);
            if ((decimal)qryKbBuchung["Betrag"] > maxBarBetrag)
            {
                KissMsg.ShowInfo(String.Format("Der Druck des Barbeleges ist nicht möglich. Der Maximalbetrag pro Barbeleg beträgt CHF {0}.", maxBarBetrag.ToString("0,000.00")));
                return;
            }

            if (DBUtil.IsEmpty(qryKbBuchung["BelegNr"]))
            {
                SqlQuery qry = DBUtil.OpenSQL(@"exec spKbGetBelegNr 'AA'");
                if (qry.Count == 0)
                {
                    KissMsg.ShowInfo("Fehler beim Erzeugen der Beleg-Nr");
                    return;
                }
                qryKbBuchung["BelegNr"] = qry.Row[0];
            }

            if (!DBUtil.IsEmpty(qryKbBuchung["BarbelegDatum"]))
            {
                if (!KissMsg.ShowQuestion("Hinweis:\r\n" +
                                          "Dieser Barbeleg wurde bereits ausgedruckt.\r\n" +
                                          "Falls er bereits eingelöst wurde: Bitte NEUEN Barbeleg im entsprechenden Budget erstellen!\r\n\r\n" +
                                          "Soll der Barbeleg nochmals gedruckt werden?"))
                    return;
            }

            if ((int)qryKbBuchung["KbBuchungStatusCode"] == 2)  //grün -> gedruckt
                qryKbBuchung["KbBuchungStatusCode"] = 4;

            if (DBUtil.IsEmpty(qryKbBuchung["BarbelegDatum"]))
                qryKbBuchung["BarbelegDatum"] = DateTime.Today;

            if (DBUtil.IsEmpty(qryKbBuchung["BarbelegGueltigVon"])) // Falls das ValutaDatum in der Zukunft liegt, wird das BarbelegGueltigVon aufs ValutaDatum gesetzt, sonst auf heute
                qryKbBuchung["BarbelegGueltigVon"] = DBUtil.IsEmpty(qryKbBuchung["ValutaDatum"]) || (DateTime)qryKbBuchung["ValutaDatum"] < DateTime.Today ? DateTime.Today : qryKbBuchung["ValutaDatum"];

            if (DBUtil.IsEmpty(qryKbBuchung["BarbelegUserID"]))
                qryKbBuchung["BarbelegUserID"] = Session.User.UserID;

            qryKbBuchung.Post();

            NamedPrm[] prms = new NamedPrm[2];
            prms[0] = new NamedPrm("KbBuchungID", qryKbBuchung["KbBuchungID"]);
            prms[1] = new NamedPrm("KasseUserID", Session.User.UserID);
            RepUtil.ExecuteReport("WhKassenbeleg", KissReportDestination.Viewer, prms);

            qryKbBuchung.Refresh();
        }

        private void FrmKasse_Load(object sender, System.EventArgs e)
        {
            //Buchungsstati laden
            repositoryItemImageComboBox1.SmallImages = KissImageList.SmallImageList;
            SqlQuery qryStati = DBUtil.OpenSQL(@"
                select *
                from   XLOVCode
                where  LOVName = 'KbBuchungsStatus' and
                       Code in (2,7,4,5,3,11,6,9,8)
                order by SortKey");
            foreach (DataRow R in qryStati.DataTable.Rows)
            {
                repositoryItemImageComboBox1.Items.Add(new DevExpress.XtraEditors.Controls.ImageComboBoxItem(
                    R["Text"].ToString(),
                    (int)R["Code"],
                    KissImageList.GetImageIndex(Convert.ToInt32(R["Value1"]))));
            }
            colStatus.ColumnEdit = repositoryItemImageComboBox1;

            // Dasselbe für edtSucheStatus
            edtSucheStatus.Properties.SmallImages = KissImageList.SmallImageList;
            edtSucheStatus.Properties.Items.Add(new DevExpress.XtraEditors.Controls.ImageComboBoxItem("", null, -1));
            foreach (DevExpress.XtraEditors.Controls.ImageComboBoxItem item in repositoryItemImageComboBox1.Items)
                edtSucheStatus.Properties.Items.Add(item);

            NewSearch();
        }

        private void FrmKasse_Print(object sender, System.EventArgs e)
        {
            btnDrucken.PerformClick();
        }

