DECLARE @searchString NVARCHAR(200); DECLARE @baPersonID INT;
-- akzeptierte Schreibweisen: -- name vorname -- name, vorname -- name,vorname -- Fall-Nummer -- Personen-Nummer SELECT @searchString = REPLACE({0}, ', ', ' '); SELECT @searchString = REPLACE(@searchString, ',', ' '); IF ({0} NOT LIKE '%[^0-9]%') BEGIN SET @baPersonID = CONVERT(INT, {0}); END;
SELECT [ID$] = PER.BaPersonID, [Klient] = Name + ISNULL(', ' + Vorname, ''), [Person ID] = PER.BaPersonID FROM dbo.BaPerson PER WITH(READUNCOMMITTED) LEFT JOIN FaFall FAL ON FAL.BaPersonID = PER.BaPersonID WHERE Name + ISNULL(' ' + Vorname,'') LIKE '%' + @searchString + '%' OR PER.BaPersonID = @baPersonID OR FAL.FaFallID = @baPersonID ORDER BY Klient; ----
