        public CtlWhNotfall(Image titelImage, string titelText, int bgFinanzplanID, int baPersonID)
            : this()
        {
            this.picTitle.Image = titelImage;
            this.BgFinanzplanID = bgFinanzplanID;
            this.BaPersonID = baPersonID;

            edtStatus.Properties.SmallImages = KissImageList.SmallImageList;
            SqlQuery qryStati = DBUtil.OpenSQL("select * from XLOVCode where LOVName = 'KbBuchungsStatus' order by SortKey");
            foreach (DataRow R in qryStati.DataTable.Rows)
            {
                edtStatus.Properties.Items.Add(new DevExpress.XtraEditors.Controls.ImageComboBoxItem(
                    R["Text"].ToString(),
                    (int)R["Code"],
                    KissImageList.GetImageIndex(Convert.ToInt32(R["Value1"]))));
            }

            this.qryHeader.Fill(qryHeader.SelectStatement, BgFinanzplanID, BaPersonID);
            this.lblTitel.Text = string.Format(this.lblTitel.Text, qryHeader["FinanzPlanVon"], qryHeader["FinanzPlanBis"], titelText);

            object BgBewilligungStatusCode = qryHeader["BgBewilligungStatusCode"];
            bool enable = (BgBewilligungStatusCode == null || (int)BgBewilligungStatusCode < (int)BgBewilligungStatus.Erteilt);

            btnBudgetGrau.BringToFront();
            btnBudgetGruen.BringToFront();
            btnBudgetRot.BringToFront();

            btnAdd.Enabled = enable;
            btnRemove.Enabled = enable;
            btnAddAll.Enabled = enable;
            btnRemoveAll.Enabled = enable;

            if (!enable)
                this.grdHaushalt.GridStyle = GridStyleType.ReadOnly;

            RefreshDisplay();

            qryBgBudget.Fill(bgFinanzplanID);
            if (qryBgBudget.Count == 1)
            {
                this.BgBudgetID = (int)qryBgBudget["BgbudgetID"];
                this.isMasterbudget = (bool)qryBgBudget["Masterbudget"];

                qryBgPosition.Fill(BgBudgetID);
            }

            SetBudgetEditMode();
            SetPositionEditMode();
        }

