        private void btnBudgetGrau_Click(object sender, System.EventArgs e)
        {
            //prüfen, ob grau stellen immer noch erlaubt
            qryBgPosition.Refresh();
            qryBgBudget.Refresh();
            if (!btnBudgetGrau.Visible) return;

            try
            {
                int budgetStatus = (int)qryBgBudget["BgBewilligungStatusCode"];
                if (budgetStatus != 5) return;

                // grünes Budget auf grau stellen
                Session.BeginTransaction();
                DBUtil.ExecSQLThrowingException(@"
                    -- NettoBuchungen
                    delete BUK
                    from   KbBuchungKostenart BUK
                           inner join BgPosition POS on POS.BgPositionID = BUK.BgPositionID
                    where  POS.BgBudgetID = {0}

                    delete KbBuchung
                    where  BgBudgetID = {0}",
                    this.BgBudgetID);

                DBUtil.ExecSQLThrowingException(@"
                    -- BruttoBuchungen
                    delete BUP
                    from   KbBuchungBruttoPerson BUP
                           inner join BgPosition POS on POS.BgPositionID = BUP.BgPositionID
                    where  POS.BgBudgetID = {0}

                    delete KbBuchungBrutto
                    where  BgBudgetID = {0}",
                    this.BgBudgetID);

                DBUtil.ExecSQLThrowingException(@"update BgBudget
                        set    BgBewilligungStatusCode = 1
                        where  BgBudgetID = {0}",
                        this.BgBudgetID);

                Session.Commit();
            }
            catch (KissCancelException ex)
            {
                Session.Rollback();
                ex.ShowMessage();
                return;
            }
            catch (Exception ex)
            {
                Session.Rollback();
                KissMsg.ShowError(ex.Message);
                return;
            }

            qryBgBudget.Refresh();
            qryBgPosition.Refresh();
            SetBudgetEditMode();
            FormController.SendMessage("FrmFall", "Action", "RefreshTree");
        }

        private void btnBudgetGruen_Click(object sender, System.EventArgs e)
        {
            try
            {
                int budgetStatus = (int)qryBgBudget["BgBewilligungStatusCode"];
                if (budgetStatus == (int)BgBewilligungStatus.Gesperrt)
                {
                    int? tageSeitBudget = DBUtil.ExecuteScalarSQLThrowingException(@"
                      SELECT MAX(DATEDIFF(d, dbo.fnLastDayOf(dbo.fnDateSerial(BUD.Jahr, BUD.Monat, 1)), dbo.fnDateOf(GETDATE())))
                      FROM dbo.KbBuchung                  BUC
                        INNER JOIN dbo.BgBudget           BUD ON BUD.BgBudgetID = BUC.BgBudgetID
                      WHERE BUC.BgBudgetID = {0} AND BUC.KbBuchungStatusCode = 7", BgBudgetID) as int?;
                    if (tageSeitBudget.HasValue && tageSeitBudget.Value > 60)
                    {
                        throw new KissErrorException(KissMsg.GetMLMessage("CtlWhBudget", "AlteRotePositionNichtGruenstellbar", "Der letzte Tag des Budgetmonats liegt mehr als 60 Tage zurück, deshalb darf dieses Budget nicht mehr freigegeben werden. Die betroffenen Belege müssen storniert werden. Falls nötig müssen die Leistungen neu erfasst werden."));
                    }

                    // rot -> grün
                    // gesperrte Belege freigeben

                    DBUtil.ExecSQLThrowingException(@"
                        UPDATE KbBuchungBrutto
                        SET    KbBuchungStatusCode = 2 /*freigegeben*/
                        WHERE  BgBudgetID = {0} AND KbBuchungTypCode IN (1,2) AND KbBuchungStatusCode = 7 /*gesperrt*/ AND BelegNr IS NULL

                        UPDATE KbBuchung
                        SET    KbBuchungStatusCode = 2 /*freigegeben*/
                        WHERE  BgBudgetID = {0} AND KbBuchungTypCode IN (1,2) AND KbBuchungStatusCode = 7 /*gesperrt*/ AND BelegNr IS NULL

                        UPDATE BgBudget SET BgBewilligungStatusCode = 5 /* ersteilt */ WHERE BgBudgetID = {0}
                        ", BgBudgetID);
                }
                else if (budgetStatus == (int)BgBewilligungStatus.Erteilt)
                {
                    return;
                }
                else if (budgetStatus == (int)BgBewilligungStatus.InVorbereitung)
                {
                    // grau -> grün
                    // Budget auf grün stellen
                    DBUtil.ExecSQLThrowingException("EXECUTE spWhBudget_CreateKbBuchung {0}, {1}, 0", this.BgBudgetID, Session.User.UserID);
                }
            }
            catch (KissCancelException ex)
            {
                ex.ShowMessage();
                return;
            }
            catch (Exception ex)
            {
                KissMsg.ShowError(ex.Message);
                return;
            }

            this.qryBgPosition.Refresh();
            SetBudgetEditMode();
            FormController.SendMessage("FrmFall", "Action", "RefreshTree");
        }

        private void btnBudgetRot_Click(object sender, System.EventArgs e)
        {
            if (!qryBgPosition.Post()) return;
            try
            {
                int BewilligungCode = (int)qryBgBudget["BgBewilligungStatusCode"];
                if (BewilligungCode != 5) return;

                // alle grünen Buchungen auf rot stellen
                DBUtil.ExecSQLThrowingException(@"
                Update BUC
                set    KbBuchungStatusCode = 7
                from   KbBuchung BUC
                       inner join KbBuchungKostenart BUK on BUK.KbBuchungID = BUC.KbBuchungID
                       inner join BgPosition         BPO on BPO.BgPositionID = BUK.BgPositionID and
                                                            BPO.BgKategorieCode in (2,100,101)
                where  BUC.BgBudgetID = {0} and
                       BUC.KbBuchungStatusCode = 2 and
                       BUC.KbBuchungTypCode in (1,2) and
                       BUC.BelegNr is null",
                qryBgBudget["BgBudgetID"]);

                qryBgPosition.Refresh();
            }
            catch (KissCancelException ex)
            {
                ex.ShowMessage();
                return;
            }
            catch (Exception ex)
            {
                KissMsg.ShowError(ex.Message);
                return;
            }

            qryBgBudget["BgBewilligungStatusCode"] = BgBewilligungStatus.Gesperrt;
            qryBgBudget.Post();
            this.qryBgPosition.Refresh();
            SetBudgetEditMode();
            FormController.SendMessage("FrmFall", "Action", "RefreshTree");
        }

