        private void edtKreditor_UserModifiedFld(object sender, KiSS4.Gui.UserModifiedFldEventArgs e)
        {
            string SearchString = edtKreditor.EditValue.ToString().Replace("*", "%").Replace("?", "_").Replace(" ", "%");

            if (DBUtil.IsEmpty(SearchString))
            {
                if (e.ButtonClicked)
                {
                    SearchString = "%";
                }
                else
                {
                    qryBgPosition["Kreditor"] = DBNull.Value;
                    qryBgPosition["ZusatzInfo"] = DBNull.Value;
                    qryBgPosition["BaZahlungswegID"] = DBNull.Value;
                    qryBgPosition["EinzahlungsscheinCode"] = DBNull.Value;
                    SetPositionEditMode();
                    return;
                }
            }

            int kategorie = (int)qryBgPosition["BgKategorieCode"];

            if (SearchString == ".")  // Auszahlung Klient/Involvierte Stellen
            {
                KissLookupDialog dlg = new KissLookupDialog();
                e.Cancel = !dlg.SearchData(@"
                    SELECT ID$          = KRE.BaZahlungswegID,
                           Person       = KRE.PersonNameVorname,
                           Zahlungsweg  = KRE.Zahlungsweg,
                           ESCode$      = KRE.EinzahlungsscheinCode,
                           Kreditor$    = KRE.PersonNameVorname,
                           ZusatzInfo$  = KRE.ZUsatzInfo
                    FROM   vwKreditor KRE
                           INNER JOIN BgFinanzPlan_BaPerson FPP ON FPP.BaPersonID = KRE.BaPersonID
                    WHERE  FPP.BgFinanzplanID = " + qryBgBudget["BgFinanzplanID"].ToString(),
                "",
                e.ButtonClicked, null, null, null);

                if (!e.Cancel)
                {
                    qryBgPosition["Kreditor"] = dlg["Kreditor$"];
                    qryBgPosition["ZusatzInfo"] = dlg["ZusatzInfo$"];
                    qryBgPosition["BaZahlungswegID"] = dlg["ID$"];
                    qryBgPosition["EinzahlungsscheinCode"] = dlg["ESCode$"];

                    if (kategorie == 100)
                        qryBgPosition["BgSpezkontoID"] = DBNull.Value;

                    SetPositionEditMode();
                }
            }
            else // Auszahlung Dritte
            {
                Common.ZH.DlgAuswahlBaZahlungsweg dlg2 = new Common.ZH.DlgAuswahlBaZahlungsweg();
                ApplicationFacade.DoEvents();

                bool transfer = false;
                dlg2.SucheBaZahlungsweg(SearchString);
                switch (dlg2.Count)
                {
                    case 0: KissMsg.ShowInfo("Keine zutreffenden Kreditor-Einträge im Institutionenstamm gefunden!");
                        break;

                    case 1: transfer = true;
                        break;

                    default: transfer = dlg2.ShowDialog(this) == DialogResult.OK;
                        break;
                }

                if (transfer)
                {
                    SqlQuery qry2 = DBUtil.OpenSQL(@"
                        select VornameName,WohnsitzStrasseHausNr,WohnsitzPLZOrt
                        from   vwPerson
                        where  BaPersonID = {0}",
                        qryBgBudget["LeistungBaPersonID"]);

                    qryBgPosition["BaZahlungswegID"] = dlg2["BaZahlungswegID"];
                    qryBgPosition["Kreditor"] = dlg2["Kreditor"];
                    qryBgPosition["ZusatzInfo"] = dlg2["ZusatzInfo"];
                    qryBgPosition["EinzahlungsscheinCode"] = dlg2["EinzahlungsscheinCode"];
                    qryBgPosition["MitteilungZeile1"] = TruncateString(qry2["VornameName"], 35);

                    if ((int)qryBgPosition["BgKategorieCode"] == 100)
                        qryBgPosition["BgSpezkontoID"] = DBNull.Value;
                }

                qryBgPosition.RefreshDisplay();
                SetPositionEditMode();
            }
        }

