        private void qryBgPosition_PositionChanged(object sender, System.EventArgs e)
        {
            if (qryBgPosition.Row.RowState == DataRowState.Added) return;

            bool gruppierung = (bool)qryBgPosition["Gruppe"] || (bool)qryBgPosition["Kategorie"];

            int kategorie = 0;
            if (!DBUtil.IsEmpty(qryBgPosition["BgKategorieCode"]))
                kategorie = (int)qryBgPosition["BgKategorieCode"];

            SetPositionEditMode();

            tabBgPosition_SelectedTabChanged(tabBgPosition.SelectedTab);
            ctlErfassungMutation1.ShowInfo();
        }

        private void repItemIstUnterstuetzt_CheckedChanged(object sender, EventArgs e)
        {
