        public CtlWhVerfuegungen(Image titelImage, string titelText, int faLeistungID)
            : this()
        {
            picTitel.Image = titelImage;
            lblTitel.Text = titelText;

            this.faLeistungID = faLeistungID;

            //Personen des Klientensystems
            edtBaPersonID.Properties.DataSource = DBUtil.OpenSQL(@"
             select Code = P.BaPersonID,
                    Text = IsNull(P.Vorname+ ' ','') + P.Name
             from   FaLeistung LEI
                    inner join FaFallPerson F on F.FaFallID = LEI.FaFallID
                    left join BaPerson P on P.BaPersonID = F.BaPersonID
             where LEI.FaLeistungID = {0}
             union all
             select null,''
             order by 2",
           faLeistungID);

            colBetrPerson.ColumnEdit = grdDokumente.GetLOVLookUpEdit((SqlQuery)edtBaPersonID.Properties.DataSource);
            colGrund.ColumnEdit = grdDokumente.GetLOVLookUpEdit((SqlQuery)edtGrund.Properties.DataSource);
            colStatus.ColumnEdit = grdDokumente.GetLOVLookUpEdit((SqlQuery)edtStatus.Properties.DataSource);

            btnAnfrage.Location = btnVorbereitung.Location;

            //this.qryWhEkEntscheid.PostCompleted += new System.EventHandler(this.qryWhEkEntscheid_PostCompleted);

            qryWhEkEntscheid.Fill(faLeistungID);
        }

