        #region Public Methods

        public override object GetContextValue(string FieldName)
        {
            switch (FieldName.ToUpper())
            {
                case "USERID": return Session.User.UserID;
                case "BETRPERSONID": return qryWhEkEntscheid["BaPersonID"];
                case "FT": return DBUtil.ExecuteScalarSQL(@"
                                    select FAL.BaPersonID
                                    from   FaFall FAL
                                           inner join FaLeistung LEI on LEI.FaFallID = FAL.FaFallID
                                    where LEI.FaLeistungID = {0}",
                             qryWhEkEntscheid["FaLeistungID"]);

                case "FALLUSERID": return DBUtil.ExecuteScalarSQL(@"
                                    select FAL.UserID
                                    from   FaFall FAL
                                           inner join FaLeistung LEI on LEI.FaFallID = FAL.FaFallID
                                    where LEI.FaLeistungID = {0}",
                                     qryWhEkEntscheid["FaLeistungID"]);

                case "FAFALLID": return DBUtil.ExecuteScalarSQL(@"
                                    select FaFallID from FaLeistung where FaLeistungID = {0}",
                                   qryWhEkEntscheid["FaLeistungID"]);

                case "WHEKENTSCHEIDID": return qryWhEkEntscheid["WhEkEntscheidID"];

            }
            return base.GetContextValue(FieldName);
        }

