        #region Private Methods

        private void CheckEnabled()
        {
            bool edit = true;
            btnVorbereitung.Visible = false;
            btnAnfrage.Visible = false;

            switch ((int)qryWhEkEntscheid["WhEkStatusCode"])
            {
                case 1: // in Vorbereitung
                    btnAnfrage.Visible = (qryWhEkEntscheid.Row.RowState != DataRowState.Added) &&
                                           !DBUtil.IsEmpty(qryWhEkEntscheid["DocumentID"]);
                    break;
                case 2: // beantragt
                    edit = DBUtil.IsEmpty(qryWhEkEntscheid["EntscheidKW"]) && DBUtil.IsEmpty(qryWhEkEntscheid["EntscheidJahr"]);
                    btnVorbereitung.Visible = edit;
                    edit = false;
                    break;
                case 3: // visiert
                    edit = false;
                    break;
                case 4: // abgelehnt
                case 5: // zurückgezogen
                    //btnAnfrage.Visible = true;
                    edit = false;
                    break;
            }

            qryWhEkEntscheid.EnableBoundFields(edit);
            qryWhEkEntscheid.CanUpdate = edit;
            qryWhEkEntscheid.CanDelete = edit;

            ctlErfassungMutation1.ShowInfo();
        }

        private void btnAnfrage_Click(object sender, System.EventArgs e)
        {
            if (!qryWhEkEntscheid.Post()) return;

            qryWhEkEntscheid["WhEkStatusCode"] = 2;
            qryWhEkEntscheid.Post();
            CheckEnabled();
        }

        private void btnVorbereitung_Click(object sender, System.EventArgs e)
        {
            if (!qryWhEkEntscheid.Post()) return;
            qryWhEkEntscheid.CanUpdate = true;
            qryWhEkEntscheid["WhEkStatusCode"] = 1;
            qryWhEkEntscheid.Post();
            CheckEnabled();
        }

        private void qryWhEkEntscheid_AfterInsert(object sender, System.EventArgs e)
        {
            qryWhEkEntscheid["FaLeistungID"] = faLeistungID;
            qryWhEkEntscheid["Datum"] = DateTime.Today;
            qryWhEkEntscheid["WhEkStatusCode"] = 1;
            ctlErfassungMutation1.ShowInfo();
            edtDatum.Focus();
        }

        private void qryWhEkEntscheid_AfterPost(object sender, System.EventArgs e)
        {
            CheckEnabled();
        }

        private void qryWhEkEntscheid_BeforePost(object sender, System.EventArgs e)
        {
            DBUtil.CheckNotNullField(edtDatum, lblDatum.Text);
            DBUtil.CheckNotNullField(edtGrund, lblGrund.Text);
            DBUtil.CheckNotNullField(edtBaPersonID, lblBaPersonID.Text);

            ctlErfassungMutation1.SetFields();
        }

        private void qryWhEkEntscheid_PositionChanged(object sender, System.EventArgs e)
        {
            CheckEnabled();
        }

