        private void ProgressStart()
        {
            Cursor = Cursors.WaitCursor;
            ApplicationFacade.DoEvents();

            DlgProgressLog.AddLine("Start der Übertragung");
            ApplicationFacade.DoEvents();
            int successCount = 0;
            int failureCount = 0;
            int count = 1;
            bool lastLineUpdateable = false;

            try
            {
                foreach (DataRow R in qryKbBuchung.DataTable.Rows)
                {
                    if (!DBUtil.IsEmpty(R["Sel"]) && (bool)R["Sel"])
                    {
                        string UserText = string.Format("Beleg {0}/{1}  {2}; CHF {3:N2}", count, totalBelegCount, R["Klient"], R["Betrag"]);

                        if (lastLineUpdateable)
                        {
                            DlgProgressLog.UpdateLastLine(UserText + ": senden ... ");
                        }
                        else
                        {
                            DlgProgressLog.AddLine(UserText + ": senden ... ");
                            lastLineUpdateable = true;
                        }
                        // alle 100 Zeilen die Belegzeile stehen lassen (für Fortschrittshistory)
                        if (count % 100 == 0)
                            lastLineUpdateable = false;

                        if (R["ZahlungForderung"].ToString() == "Z")	// Z = Zahlung = Kreditor
                        {
                            try
                            {
                                bool success = false;
                                if ((int)R["AuszahlungsArtCode"] == 103)
                                {
                                    // Barbelege bekommen immer ein neues Valutadatum, und zwar das aktuelle Tagesdatum
                                    success = KiSS4.FAMOZ.PSCDServices.PSCDInterface.SubmitWhBuchungsstoff(new int[] { (int)R["KbBuchungID"] }, null, DateTime.Today);
                                }
                                else if (dateBuchungsdatum.EditValue == null)
                                {
                                    // Es wurde kein neues Valutadatum angegeben. In diesem Fall wird das bestehende Valutadatum des Belegs verwendet
                                    success = KiSS4.FAMOZ.PSCDServices.PSCDInterface.SubmitWhBuchungsstoff(new int[] { (int)R["KbBuchungID"] }, null);
                                }
                                else
                                {
                                    success = KiSS4.FAMOZ.PSCDServices.PSCDInterface.SubmitWhBuchungsstoff(new int[] { (int)R["KbBuchungID"] }, null, dateBuchungsdatum.DateTime);
                                }

                                if (success)
                                    successCount++;
                                else
                                    failureCount++;
                            }
                            catch (Exception ex)
                            {
                                if (lastLineUpdateable)
                                    DlgProgressLog.UpdateLastLine(ex.Message);
                                else
                                    DlgProgressLog.AddLine(ex.Message);

                                lastLineUpdateable = false;
                                failureCount++;
                            }
                            count++;
                        }
                        else if (R["ZahlungForderung"].ToString() == "F" && !DBUtil.IsEmpty(R["KbBuchungBruttoID"]))  // F = Forderung = Debitor
                        {
                            try
                            {
                                bool success = false;
                                // Das ValutaDatum wird bei Debitoren nicht mitgegeben
                                success = KiSS4.FAMOZ.PSCDServices.PSCDInterface.SubmitWhBuchungsstoff(null, new int[] { (int)R["KbBuchungBruttoID"] });

                                if (success)
                                    successCount++;
                                else
                                    failureCount++;
                            }
                            catch (Exception ex)
                            {
                                if (lastLineUpdateable)
                                    DlgProgressLog.UpdateLastLine(ex.Message);
                                else
                                    DlgProgressLog.AddLine(ex.Message);
                                lastLineUpdateable = false;
                                failureCount++;
                            }
                            count++;
                        }
                        else
                        {
                            DlgProgressLog.AddError(String.Format("Fehler: Ungültiger Wert ({0})", R["ZahlungForderung"]));
                        }
                    }
                }
                DlgProgressLog.AddLine("Ende der Übertragung");
                DlgProgressLog.AddLine(string.Format("- {0} Beleg(e) erfolgreich übermittelt", successCount));
                DlgProgressLog.AddLine(string.Format("- {0} Beleg(e) fehlerhaft", failureCount));
            }
            catch (CancelledByUserException)
            {
                KissMsg.ShowInfo("Abbruch durch Benutzer/in\r\n\r\n" +
                                 string.Format("- {0} Beleg(e) erfolgreich übermittelt", successCount) + "\r\n" +
                                 string.Format("- {0} Beleg(e) fehlerhaft", failureCount));
            }

            /*
            foreach( int nettoID in IDListNetto )
            {
                if( ++count % 50 == 0 )
                {
                    DlgProgressLog.AddLine(string.Format("{0} Belege bearbeitet", count));
                }
            }
            foreach( int bruttoID in IDListBrutto )
            {
                if( ++count % 50 == 0 )
                {
                    DlgProgressLog.AddLine(string.Format("{0} Belege bearbeitet", count));
                }
            }
            */

            Cursor = Cursors.Default;

            DlgProgressLog.EndProgress();
            DlgProgressLog.ShowTopMost();
        }

