        #region Protected Methods

        protected override void NewSearch()
        {
            base.NewSearch();

            DateTime d = DateTime.Today.AddDays(1); //heute + 1 Tage
            if ((int)d.DayOfWeek == 6) d = d.AddDays(2); //Samstag -> Montag
            if ((int)d.DayOfWeek == 0) d = d.AddDays(1); //Sonntag -> Montag
            edtSucheValutaBis.EditValue = d;
        }

        #endregion

        #region Private Methods

        private void CalculateTotal()
        {
            int Count = 0;
            decimal Total = 0;

            foreach (DataRow R in qryKbBuchungBrutto.DataTable.Rows)
            {
                if (!DBUtil.IsEmpty(R["Sel"]) && (bool)R["Sel"])
                {
                    Count++;
                    Total += (decimal)R["BetragAbs"];
                }
            }

            edtTotal.EditValue = Total;
            edtAnzahl.EditValue = Count;

            btnTransfer.Enabled = (Count > 0);
            btnTransferAsync.Enabled = btnTransfer.Enabled;
        }

        private void CtlWhZahllaufBrutto_Load(object sender, System.EventArgs e)
        {
            DateTime d = DateTime.Today.AddDays(1); //heute + 1 Tage
            if ((int)d.DayOfWeek == 6) d = d.AddDays(2); //Samstag -> Montag
            if ((int)d.DayOfWeek == 0) d = d.AddDays(1); //Sonntag -> Montag
            dateBuchungsdatum.EditValue = d;

            //colTyp.ColumnEdit = grdDoc.GetLOVLookUpEdit("BgDokumentTyp");

            //Buchungsstati laden
            repositoryItemImageComboBox1.SmallImages = KissImageList.SmallImageList;
            SqlQuery qryStati = DBUtil.OpenSQL("select * from XLOVCode where LOVName = 'KbBuchungsStatus' order by SortKey");
            foreach (DataRow R in qryStati.DataTable.Rows)
            {
                repositoryItemImageComboBox1.Items.Add(new DevExpress.XtraEditors.Controls.ImageComboBoxItem(
                    R["Text"].ToString(),
                    (int)R["Code"],
                    KissImageList.GetImageIndex(Convert.ToInt32(R["Value1"]))));
            }

            grvZahllauf.CellValueChanging += new DevExpress.XtraGrid.Views.Base.CellValueChangedEventHandler(grvZahllauf_CellValueChanging);
            grvZahllauf.CustomRowCellEdit += new DevExpress.XtraGrid.Views.Grid.CustomRowCellEditEventHandler(this.grvZahllauf_CustomRowCellEdit);

            NewSearch();
        }

        private void ProgressEnd()
        {
            qryKbBuchungBrutto.Refresh();
        }

        private void ProgressStart()
        {
            Cursor = Cursors.WaitCursor;
            ApplicationFacade.DoEvents();

            DlgProgressLog.AddLine("Start der Übertragung");
            ApplicationFacade.DoEvents();
            int successCount = 0;
            int failureCount = 0;
            int count = 1;
            bool lastLineUpdateable = false;

            try
            {
                foreach (DataRow R in qryKbBuchungBrutto.DataTable.Rows)
                {
                    if (!DBUtil.IsEmpty(R["Sel"]) && (bool)R["Sel"])
                    {
                        string UserText = string.Format("Beleg {0}/{1}  {2}; CHF {3:N2}", count, totalBelegCount, R["Klient"], R["Betrag"]);

                        if (lastLineUpdateable)
                        {
                            DlgProgressLog.UpdateLastLine(UserText + ": senden ... ");
                        }
                        else
                        {
                            DlgProgressLog.AddLine(UserText + ": senden ... ");
                            lastLineUpdateable = true;
                        }
                        // alle 100 Zeilen die Belegzeile stehen lassen (für Fortschrittshistory)
                        if (count % 100 == 0)
                            lastLineUpdateable = false;

                        try
                        {
                            bool success = false;
                            if (dateBuchungsdatum.EditValue == null)
                            {
                                success = KiSS4.FAMOZ.PSCDServices.PSCDInterface.SubmitWhBuchungsstoff(null, new int[] { (int)R["KbBuchungBruttoID"] });
                            }
                            else
                            {
                                success = KiSS4.FAMOZ.PSCDServices.PSCDInterface.SubmitWhBuchungsstoff(null, new int[] { (int)R["KbBuchungBruttoID"] }, dateBuchungsdatum.DateTime);
                            }

                            if (success)
                                successCount++;
                            else
                                failureCount++;
                        }
                        catch (Exception ex)
                        {
                            if (lastLineUpdateable)
                                DlgProgressLog.UpdateLastLine(ex.Message);
                            else
                                DlgProgressLog.AddLine(ex.Message);
                            lastLineUpdateable = false;
                            failureCount++;
                        }
                        count++;
                    }
                }
                DlgProgressLog.AddLine("Ende der Übertragung");
                DlgProgressLog.AddLine(string.Format("- {0} Bruttobeleg(e) erfolgreich übermittelt", successCount));
                DlgProgressLog.AddLine(string.Format("- {0} Bruttobeleg(e) fehlerhaft", failureCount));
            }
            catch (CancelledByUserException)
            {
                KissMsg.ShowInfo("Abbruch durch Benutzer/in\r\n\r\n" +
                                 string.Format("- {0} Bruttobeleg(e) erfolgreich übermittelt", successCount) + "\r\n" +
                                 string.Format("- {0} Bruttobeleg(e) fehlerhaft", failureCount));
            }

            Cursor = Cursors.Default;

            DlgProgressLog.EndProgress();
            DlgProgressLog.ShowTopMost();
        }

        private void btnAlle_Click(object sender, System.EventArgs e)
        {
            Cursor = Cursors.WaitCursor;

            foreach (DataRow R in qryKbBuchungBrutto.DataTable.Rows)
            {
                if (!DBUtil.IsEmpty(R["Sel"])) R["Sel"] = true;
            }
            CalculateTotal();
            Cursor = Cursors.Default;
        }

        private void btnKeine_Click(object sender, System.EventArgs e)
        {
            Cursor = Cursors.WaitCursor;

            foreach (DataRow R in qryKbBuchungBrutto.DataTable.Rows)
            {
                if (!DBUtil.IsEmpty(R["Sel"])) R["Sel"] = false;
            }
            CalculateTotal();
            Cursor = Cursors.Default;
        }

        private void btnMonatsbudget_Click(object sender, System.EventArgs e)
        {
            if (qryKbBuchungBrutto.Count == 0 || DBUtil.IsEmpty(qryKbBuchungBrutto["JumpToMBPfad"])) return;

            FormController.OpenForm("FrmFall", "Action", "JumpToNodeByFieldValue",
                        "BaPersonID", qryKbBuchungBrutto["FallBaPersonID"],
                        "ModulID", 3,
                        "FieldValue", qryKbBuchungBrutto["JumpToMBPfad"]);
        }

        private void btnTransfer_Click(object sender, System.EventArgs e)
        {
            try
            {
                DBUtil.ThrowExceptionOnOpenTransaction();
            }
            catch (Exception ex)
            {
                KissMsg.ShowError(ex.Message);
                this.OnSearch();
                return;
            }

            System.Collections.Generic.List<int> IDListBrutto = new System.Collections.Generic.List<int>();

            decimal TotalBrutto = 0;

            foreach (DataRow R in qryKbBuchungBrutto.DataTable.Rows)
            {
                if (!DBUtil.IsEmpty(R["Sel"]) && (bool)R["Sel"])
                {
                    IDListBrutto.Add((int)R["KbBuchungBruttoID"]);
                    TotalBrutto += (decimal)R["BetragAbs"];
                }
            }
            if (IDListBrutto.Count == 0) return;

            totalBelegCount = IDListBrutto.Count;

            string msg;
            if (IDListBrutto.Count == 1)
                msg = string.Format("Soll 1 Bruttobeleg mit dem Betrag von CHF {0:0,0.00} an PSCD übermittelt werden?", TotalBrutto);
            else
                msg = string.Format("Sollen {0} Bruttobelege mit dem Total von CHF {1:0,0.00} an PSCD übermittelt werden?", IDListBrutto.Count, TotalBrutto);

            if (KissMsg.ShowQuestion(msg))
            {
                DlgProgressLog.Show("Zahllauf", 700, 500, new ProgressEventHandler(ProgressStart), new ProgressEventHandler(ProgressEnd), Session.MainForm);
            }
        }

        private void grdZahllauf_Click(object sender, System.EventArgs e)
        {
            qryKbBuchungBrutto.EndCurrentEdit();
            CalculateTotal();
        }

        private void grvZahllauf_CellValueChanging(object sender, DevExpress.XtraGrid.Views.Base.CellValueChangedEventArgs e)
        {
            if (e.Column != colSelektion) return;
            grvZahllauf.SetRowCellValue(e.RowHandle, colSelektion, e.Value);
            CalculateTotal();
        }

        private void grvZahllauf_CustomRowCellEdit(object sender, DevExpress.XtraGrid.Views.Grid.CustomRowCellEditEventArgs e)
        {
            if (e.Column == colSelektion)
            {
                if (DBUtil.IsEmpty(grvZahllauf.GetRowCellValue(e.RowHandle, e.Column)))
                    e.RepositoryItem = repositoryItemTextEdit1;
                else
                    e.RepositoryItem = repositoryItemCheckEdit1;
            }
        }

        private void qryKbBuchungBrutto_AfterFill(object sender, System.EventArgs e)
        {
            CalculateTotal();
        }

        private void qryKbBuchungBrutto_PositionChanged(object sender, System.EventArgs e)
        {
            btnMonatsbudget.Enabled = !DBUtil.IsEmpty(qryKbBuchungBrutto["JumpToMBPfad"]);
        }

        #endregion
    }
