        #region Constructors

        public DlgWhFinanzplanBeenden(int BgFinanzplanID)
            : this()
        {
            qryBgBewilligung.Fill("SELECT TOP 0 * FROM BgBewilligung");
            qryBgBewilligung.Insert(qryBgBewilligung.TableName);

            qryBgBewilligung["BgFinanzPlanID"] = BgFinanzplanID;

            SqlQuery qry = DBUtil.OpenSQL(@"
                Select AnzahlGraueFPs = count(FPL.FaLeistungID) from BgFinanzplan FPL
                inner join FaLeistung LEI ON LEI.FaLeistungID = FPL.FaLeistungID
                inner join BgFinanzplan FPL2 ON FPL2.FaLeistungID = LEI.FaLeistungID
                where FPL.BgBewilligungstatusCode = 1 and LEI.DatumBis IS NULL and FPL2.BgFinanzplanID = {0}
                group by FPL.FaLeistungID", BgFinanzplanID);

            if (qry.Count > 0)
            {
                // Wir erlauben nur einen grauen FP
                edtNeuerFPinnerhalbLE.Visible = false;
                lblKopie.Text = "Es existiert bereits ein grauer Finanzplan, daher kann kein neuer Finanzplan erstellt werden.";
            }
        }

