        private void cmdYes_Click(object sender, System.EventArgs e)
        {
            ActiveSQLQuery = qryBgBewilligung;

            if (DBUtil.IsEmpty(edtDatum.EditValue))
            {
                KissMsg.ShowError("DlgWhFinanzplanBeenden", "DatumLeer", "'Beenden am' muss ausgefüllt sein!");
                return;
            }

            if (((DateTime)edtDatum.EditValue).AddDays(1).Day != 1)
            {
                KissMsg.ShowInfo("Das Abschlussdatum muss immer der letzte Tag eines Monats sein.");
                return;
            }

            if (this.datum < (DateTime)edtDatum.EditValue)
            {
                KissMsg.ShowInfo("DlgWhFinanzplanBeenden", "AbschlussDatumFehler", "Das gewünschte Abschlussdatum liegt nach dem geplanten Abschlussdatum");
                return;
            }
            else if (mindatum != null && edtDatum.DateTime < (DateTime)mindatum)
            {
                KissMsg.ShowInfo("DlgWhFinanzplanBeenden", "BeendenFruehestensMoeglich", "Der Finanzplan kann frühestens auf den {0:d} beendet werden (aufgrund grüner/roter Monatsbudgets)", 0, 0, mindatum);
                return;
            }

            qryBgBewilligung["PerDatum"] = edtDatum.EditValue;
            qryBgBewilligung["BgBewilligungCode"] = 5;
            qryBgBewilligung["UserID"] = Session.User.UserID;
            qryBgBewilligung["Datum"] = DateTime.Now;

            this.Datum = Convert.ToDateTime(qryBgBewilligung["PerDatum"]);

            this.userCancel = false;
            this.DialogResult = DialogResult.OK;
            this.Close();
        }

