        private void FrmWhBetreibungskosten_Load(object sender, System.EventArgs e)
        {
            // Fenstertitel setzen

            //Buchungsstati laden
            repositoryItemImageComboBox2.SmallImages = KissImageList.SmallImageList;
            SqlQuery qryStati = DBUtil.OpenSQL("select * from XLOVCode where LOVName = 'KbBuchungsStatus' order by SortKey");
            foreach (DataRow R in qryStati.DataTable.Rows)
            {
                repositoryItemImageComboBox2.Items.Add(new DevExpress.XtraEditors.Controls.ImageComboBoxItem(
                    R["Text"].ToString(),
                    (int)R["Code"],
                    KissImageList.GetImageIndex(Convert.ToInt32(R["Value1"]))));
            }
            colStatus.ColumnEdit = repositoryItemImageComboBox2;

            // Dasselbe für edtSucheStatus
            edtSucheStatus.Properties.SmallImages = KissImageList.SmallImageList;
            edtSucheStatus.Properties.Items.Add(new DevExpress.XtraEditors.Controls.ImageComboBoxItem("", null, -1));

            foreach (DevExpress.XtraEditors.Controls.ImageComboBoxItem item in repositoryItemImageComboBox2.Items)
                edtSucheStatus.Properties.Items.Add(item);

            grvBetreibungskosten.CellValueChanging += new DevExpress.XtraGrid.Views.Base.CellValueChangedEventHandler(grvBetreibungskosten_CellValueChanging);
            grvBetreibungskosten.CustomRowCellEdit += new DevExpress.XtraGrid.Views.Grid.CustomRowCellEditEventHandler(this.grvBetreibungskosten_CustomRowCellEdit);

            edtBetrag.Properties.DisplayFormat.FormatString = "N2";
            edtBetrag.Properties.EditFormat.FormatString = "N2";

            modulAAktiv = (bool)DBUtil.ExecuteScalarSQLThrowingException(@"SELECT Licensed FROM XModul WHERE ModulID = {0}", ModulID.I);
            modulWAktiv = (bool)DBUtil.ExecuteScalarSQLThrowingException(@"SELECT Licensed FROM XModul WHERE ModulID = {0}", ModulID.S);
            modulA = DBUtil.UserHasRight("FrmWhBetreibungskosten_ModulA") && modulAAktiv;
            modulW = DBUtil.UserHasRight("FrmWhBetreibungskosten_ModulW") && modulWAktiv;

            edtSucheModulA.Enabled = (modulA && modulW);
            edtSucheModulW.Enabled = (modulA && modulW);

            edtSucheModulA.Visible = (modulA && modulW);
            edtSucheModulW.Visible = (modulA && modulW);

            edtSucheModulA.Checked = modulA;
            edtSucheModulW.Checked = modulW;

            qryKbBuchung.EnableBoundFields(false);

            tabBuchung.SelectedTabIndex = 0;
            tabControlSearch.SelectedTabIndex = 1;

            NewSearch();
        }

        private void btnAlle_Click(object sender, System.EventArgs e)
        {
            if (!qryKbBuchung.Post()) return;

            Cursor = Cursors.WaitCursor;
            foreach (DataRow R in qryKbBuchung.DataTable.Rows)
            {
                if (!DBUtil.IsEmpty(R["Sel"]))
                {
                    R["Sel"] = true;
                    R.AcceptChanges();
                }
            }
            qryKbBuchung.RowModified = false;
            CalculateTotal();
            Cursor = Cursors.Default;
        }

        private void btnKeine_Click(object sender, System.EventArgs e)
