        private void edtKreditor_UserModifiedFld(object sender, KiSS4.Gui.UserModifiedFldEventArgs e)
        {
            string SearchString = edtKreditor.Text.Replace("*", "%").Replace("?", "_").Replace(" ", "%");

            if (DBUtil.IsEmpty(SearchString))
            {
                if (e.ButtonClicked)
                {
                    SearchString = "%";
                }
                else
                {
                    qryKbBuchung["Kreditor"] = DBNull.Value;
                    qryKbBuchung["ZusatzInfo"] = DBNull.Value;
                    qryKbBuchung["BaZahlungswegID"] = DBNull.Value;
                    qryKbBuchung["EinzahlungsscheinCode"] = DBNull.Value;
                    qryKbBuchung["ReferenzNummer"] = DBNull.Value;
                    qryKbBuchung["MitteilungZeile1"] = DBNull.Value;
                    qryKbBuchung["MitteilungZeile2"] = DBNull.Value;
                    qryKbBuchung["MitteilungZeile3"] = DBNull.Value;
                    SetEditMode();
                    return;
                }
            }
            else
            {
                Common.ZH.DlgAuswahlBaZahlungsweg dlg2 = new Common.ZH.DlgAuswahlBaZahlungsweg();
                ApplicationFacade.DoEvents();

                bool transfer = false;
                dlg2.SucheBaZahlungsweg(SearchString);
                switch (dlg2.Count)
                {
                    case 0: KissMsg.ShowInfo("Keine zutreffenden Kreditor-Einträge im Institutionenstamm gefunden!");
                        break;

                    case 1: transfer = true;
                        break;

                    default: transfer = dlg2.ShowDialog(this) == DialogResult.OK;
                        break;
                }

                if (transfer)
                {
                    qryKbBuchung["BaZahlungswegID"] = dlg2["BaZahlungswegID"];
                    qryKbBuchung["Kreditor"] = dlg2["Kreditor"];
                    qryKbBuchung["ZusatzInfo"] = dlg2["ZusatzInfo"];
                    qryKbBuchung["EinzahlungsscheinCode"] = dlg2["EinzahlungsscheinCode"];

                    if ((int)qryKbBuchung["EinzahlungsscheinCode"] != 1) // != oranger ES
                    {
                        SqlQuery qry2 = DBUtil.OpenSQL(@"
                            select Text = 'Schuldner: ' + NameVorname
                            from   vwPerson
                            where  BaPersonID = {0}",
                            qryKbBuchung["BaPersonID"]);

                        qryKbBuchung["MitteilungZeile1"] = TruncateString(qry2["Text"], 35);
                        qryKbBuchung["ReferenzNummer"] = DBNull.Value;
                    }
                    else
                    {
                        qryKbBuchung["MitteilungZeile1"] = DBNull.Value;
                        qryKbBuchung["MitteilungZeile2"] = DBNull.Value;
                        qryKbBuchung["MitteilungZeile3"] = DBNull.Value;
                    }
                }

                qryKbBuchung.RefreshDisplay();
                SetEditMode();
            }
        }

        private void edtSchuldner_UserModifiedFld(object sender, KiSS4.Gui.UserModifiedFldEventArgs e)
        {
            string SearchString = edtSchuldner.EditValue.ToString().Replace("*", "%").Replace("?", "_").Replace(" ", "%");

            if (DBUtil.IsEmpty(SearchString))
            {
                if (e.ButtonClicked)
                {
                    SearchString = "%";
                }
                else
                {
                    qryKbBuchung["Schuldner"] = null;
                    qryKbBuchung["FaLeistungID"] = null;
                    qryKbBuchung["Adresse"] = null;
                    qryKbBuchung["FaProzessCode"] = null;
                    return;
                }
            }

            int SearchNr = 0;

            Int32.TryParse(SearchString, out SearchNr);

            string sql = @"

              SELECT [Pers.-Nr.]         = PRS.BaPersonID,
                     Name                = DisplayText,
                     Adresse             = PRS.Wohnsitz,
                     Leistung            = PRO.Text,
                     LA                  = KOA.KontoNr,
                     Von                 = LEI.DatumVon,
                     Bis                 = LEI.DatumBis,
                     Name$               = PRS.NameVorname,
                     Leistung$           = PRO.Text + isnull(', VG ' + convert(varchar,LEI.PscdVertragsgegenstandID),''),
                     BaPersonID$         = PRS.BaPersonID,
                     FaLeistungID$       = LEI.FaLeistungID,
                     BgKostenartID$      = KOA.BgKostenartID,
                     Kostenart$          = KOA.KontoNr + ' ' + KOA.Name,
                     BgSplittingartCode$ = KOA.BgSplittingartCode,
                     KOA$                = KOA.KontoNr,
                     ModulID$            = KOA.ModulID,
                     Kostenstelle$       = ORG.Kostenstelle,
                     FaFallID$           = LEI.FaFallID,
                     FallBaPersonID$     = FAL.BaPersonID,
                     FaProzessCode$      = LEI.FaProzessCode
              FROM   FaLeistung LEI
                     inner join vwPerson    PRS on PRS.BaPersonID = LEI.BaPersonID
                     inner join BgKostenart KOA on KOA.KontoNr = CASE
                                                                 WHEN LEI.FaProzessCode IN (301,304)        THEN '366' -- W-Inkasso VU/UB
                                                                 WHEN LEI.FaProzessCode = 302               THEN '365' -- W-Inkasso RE
                                                                 --WHEN LEI.FaProzessCode BETWEEN 405 AND 420 THEN '612' -- A-Inkassi
                                                                 END
                     left  join XLOVCode      PRO on PRO.LOVName = 'FaProzess' AND PRO.Code = LEI.FaProzessCode
                     left  join XOrgUnit_User O2U on LEI.UserID = O2U.UserID and O2U.OrgUnitMemberCode = 2 /*Mitglied*/
                     left  join XOrgUnit      ORG on ORG.OrgUnitID = O2U.OrgUnitID
                     inner join FaFall        FAL on FAL.FaFallID = LEI.FaFallID";

            if (SearchNr != 0)
                sql += " WHERE PRS.BaPersonID = {0} OR LEI.FaFallID = {0}";
            else
                sql += " WHERE PRS.NameVorname LIKE '%' + {0} + '%'";

            //if (modulW && modulA)
            //    sql += " AND LEI.FaProzessCode in (301,302,304,405,406,407,408,409,410)";
            //else if (modulW)
            sql += " AND LEI.FaProzessCode in (301,302,304)";
            //else if (modulA)
            //    sql += " AND LEI.FaProzessCode in (405,406,407,408,409,410)";
            //else
            //    sql += " AND LEI.FaProzessCode = -1";

            sql += " ORDER BY Name";

            KissLookupDialog dlg = new KissLookupDialog();
            e.Cancel = !dlg.SearchData(sql, SearchString, e.ButtonClicked, null, null, null);

            if (!e.Cancel)
            {
                qryKbBuchung["Schuldner"] = dlg["Name$"];
                qryKbBuchung["BaPersonID"] = dlg["BaPersonID$"];
                qryKbBuchung["Leistung"] = dlg["Leistung$"];
                qryKbBuchung["FaLeistungID"] = dlg["FaLeistungID$"];
                qryKbBuchung["Adresse"] = dlg["Adresse"];
                qryKbBuchung["Kostenart"] = dlg["Kostenart$"];
                qryKbBuchung["BgKostenartID"] = dlg["BgKostenartID$"];
                qryKbBuchung["BgSplittingartCode"] = dlg["BgSplittingartCode$"];
                qryKbBuchung["KOA"] = dlg["KOA$"];
                qryKbBuchung["ModulID"] = dlg["ModulID$"];
                qryKbBuchung["Kostenstelle"] = dlg["Kostenstelle$"];
                qryKbBuchung["FaFallID"] = dlg["FaFallID$"];
                qryKbBuchung["FallBaPersonID"] = dlg["FallBaPersonID$"];
                qryKbBuchung["Adresse"] = dlg["Adresse"];
                qryKbBuchung["FaProzessCode"] = dlg["FaProzessCode$"];
                SetVerwendungsPeriode();
                SetEditMode();
            }
        }

