        private void qryKbBuchung_AfterPost(object sender, System.EventArgs e)
        {
            try
            {
                Session.BeginTransaction();

                //DetailBeleg speichern
                //Löschen der alten Daten und neuen Datensatz anlegen
                DBUtil.ExecSQLThrowingException(@"
                    DELETE KbBuchungKostenart
                    WHERE KbBuchungID = {0}

                    INSERT KbBuchungKostenart(KbBuchungID,BgKostenartID,BaPersonID,
                                              Buchungstext,Betrag,VerwPeriodeVon,VerwPeriodeBis,
                                              KontoNr,PositionImBeleg,Hauptvorgang,Teilvorgang,Belegart)
                    SELECT {0},{1},{2},{3},{4},{5},{6},KontoNr,1,Hauptvorgang,Teilvorgang,Belegart
                    FROM   dbo.BgKostenart
                    WHERE  BgKostenartID = {1}",
                    qryKbBuchung["KbBuchungID"],
                    qryKbBuchung["BgKostenartID"],
                    qryKbBuchung["BaPersonID"],
                    qryKbBuchung["Text"],
                    qryKbBuchung["Betrag"],
                    qryKbBuchung["VerwPeriodeVon"],
                    qryKbBuchung["VerwPeriodeBis"]);
            }
            catch (Exception ex)
            {
                Session.Rollback();
                KissMsg.ShowInfo(ex.Message);
                return;
            }
            Session.Commit();

            if (!qryKbDokumente.Post())
            {
                tabBuchung.SelectedTabIndex = 1;
                throw new KissCancelException();
            }

            CalculateTotal();
        }

