        private void qryKbBuchung_BeforePost(object sender, System.EventArgs e)
        {
            // check must fields
            DBUtil.CheckNotNullField(edtSchuldner, lblSchuldner.Text);
            DBUtil.CheckNotNullField(edtKostenart, lblKostenart.Text);
            DBUtil.CheckNotNullField(edtBuchungstext, lblBuchungstext.Text);
            DBUtil.CheckNotNullField(edtBetrag, lblBetrag.Text);

            if (Convert.ToDecimal(qryKbBuchung["Betrag"]) <= Decimal.Zero)
            {
                edtBetrag.Focus();
                throw new KissInfoException("Der Betrag darf nicht 0.00 oder negativ sein!");
            }

            //Valutadatum
            qryKbBuchung["ValutaDatum"] = edtValutaDatum.EditValue; //get pending edit changes
            DBUtil.CheckNotNullField(edtValutaDatum, lblValutaDatum.Text);
            //für FAMOZ: Valutadatum muss zwischen heute und heute + 2 Monate liegen
            if (qryKbBuchung.ColumnModified("ValutaDatum") && ((DateTime)qryKbBuchung["ValutaDatum"]) < DateTime.Today)
            {
                edtValutaDatum.Focus();
                throw new KissInfoException("Das Valutadatum darf nicht in der Vergangenheit liegen!");
            }

            if (qryKbBuchung.ColumnModified("ValutaDatum") && ((DateTime)qryKbBuchung["ValutaDatum"]) > DateTime.Today.AddMonths(2))
            {
                edtValutaDatum.Focus();
                throw new KissInfoException("Das Valutadatum darf nicht mehr als 2 Monate in der Zukunft liegen!");
            }

            //Referenznummer
            if (edtReferenzNummer.EditMode == EditModeType.Normal)
            {
                DBUtil.CheckNotNullField(edtReferenzNummer, lblReferenzNummer.Text);

                SqlQuery qry = DBUtil.OpenSQL("select PostKontoNummer from BaZahlungsweg where BaZahlungswegID = {0}", qryKbBuchung["BaZahlungswegID"]);

                if (qry.Count == 1 && !edtReferenzNummer.ValidateReferenzNummer(qry["PostKontoNummer"].ToString()))
                {
                    edtReferenzNummer.Focus();
                    throw new KissCancelException();
                }
            }

            //Verwendungsperiode
            int BgSplittingArtCode = ShUtil.GetCode(qryKbBuchung["BgSplittingArtCode"]);
            switch (BgSplittingArtCode)
            {
                case 1: //Taggenau
                case 2: //Monat
                    DBUtil.CheckNotNullField(edtVerwPeriodeVon, "Verwendungsperiode von");
                    DBUtil.CheckNotNullField(edtVerwPeriodeBis, "Verwendungsperiode bis");
                    if ((DateTime)edtVerwPeriodeBis.EditValue < (DateTime)edtVerwPeriodeVon.EditValue)
                    {
                        throw new KissInfoException("'Verwendungsperiode von' muss kleiner sein als 'Verwendungsperiode bis'.");
                    }
                    break;

                case 3: //Valuta
                    qryKbBuchung["VerwPeriodeVon"] = qryKbBuchung["ValutaDatum"];
                    qryKbBuchung["VerwPeriodeBis"] = qryKbBuchung["ValutaDatum"];
                    break;

                case 4: //Entscheid
                    DBUtil.CheckNotNullField(edtVerwPeriodeVon, "Verwendungsperiode von");
                    qryKbBuchung["VerwPeriodeBis"] = qryKbBuchung["VerwPeriodeVon"];
                    break;
            }

            //Teilnehmernummer bzw. PCKontoNr
            qryKbBuchung["PCKontoNr"] = DBUtil.ExecuteScalarSQL(@"
                select PostKontoNummer
                from   BaZahlungsweg
                where  BaZahlungswegID = {0}",
                qryKbBuchung["BaZahlungswegID"]);

            //Buchungsperiode
            qryKbBuchung["KbPeriodeID"] = DBUtil.ExecuteScalarSQL(@"
                select KbPeriodeID
                from   KbPeriode
                where  KbMandantCode = 1 and /*für ZH nur Mandant 1*/
                       PeriodeStatusCode = 1 and /*offen*/
                       {0} between PeriodeVon and PeriodeBis",
                DateTime.Today);

            if (DBUtil.IsEmpty(qryKbBuchung["KbPeriodeID"]))
            {
                throw new KissInfoException("Es existiert keine aktuelle Buchungsperiode!");
            }

            //Kreditorkonto ins Habenkonto
            qryKbBuchung["HabenKtoNr"] = DBUtil.ExecuteScalarSQL(@"
                select top 1 KontoNr
                from   KbKonto
                where  KbPeriodeID = {0} and ',' + KbKontoartCodes + ',' LIKE '%,30,%'",
                qryKbBuchung["KbPeriodeID"]);

            qryKbBuchung["KbAuszahlungsArtCode"] = 101; //elektronisch
            qryKbBuchung["KbBuchungTypCode"] = 2; //manuell
            qryKbBuchung["BelegDatum"] = qryKbBuchung["ValutaDatum"];

            qryKbBuchung["MA"] = Session.User.LogonName;

            //Selektion Checkbox
            if (DBUtil.IsEmpty(qryKbBuchung["Sel"]))
                qryKbBuchung["Sel"] = false;

            ctlErfassungMutation1.SetFields();
        }

