        void IBelegleser.ProcessBelegleser(Belegleser beleg)
        {
            if (qryKbBuchung.Count == 0) return;

            int status = ShUtil.GetCode(qryKbBuchung["KbBuchungStatusCode"]);
            if (qryKbBuchung.Row.RowState != DataRowState.Added && (status != 1 && status != 14))
            {
                KissMsg.ShowInfo("Neue Daten vom Belegleser: Der Status der Buchung erlaubt keine Änderung der erfassten Daten");
                return;
            }

            Common.ZH.DlgAuswahlBaZahlungsweg dlg = new Common.ZH.DlgAuswahlBaZahlungsweg();
            ApplicationFacade.DoEvents();

            bool transfer = false;
            dlg.SucheBaZahlungsweg(beleg);
            switch (dlg.Count)
            {
                case 0: KissMsg.ShowInfo("Keine zutreffenden Kreditor-Einträge im Institutionenstamm gefunden!");
                    if (beleg.Betrag > 0)
                        qryKbBuchung["BetragSpezial"] = beleg.Betrag;
                    qryKbBuchung["ReferenzNummer"] = beleg.ReferenzNummer;
                    break;

                case 1: transfer = true;
                    break;

                default: transfer = dlg.ShowDialog(this) == DialogResult.OK;
                    break;
            }

            if (transfer)
            {
                qryKbBuchung["BaZahlungswegID"] = dlg["BaZahlungswegID"];
                qryKbBuchung["Kreditor"] = dlg["Kreditor"];
                qryKbBuchung["ZusatzInfo"] = dlg["ZusatzInfo"];
                if (beleg.Betrag > 0)
                    qryKbBuchung["Betrag"] = beleg.Betrag;
                qryKbBuchung["ReferenzNummer"] = beleg.ReferenzNummer;
                qryKbBuchung["EinzahlungsscheinCode"] = dlg["EinzahlungsscheinCode"];

                if ((int)qryKbBuchung["EinzahlungsscheinCode"] != 1) // != oranger ES
                {
                    SqlQuery qry2 = DBUtil.OpenSQL(@"
                        select Text = 'Schuldner: ' + NameVorname
                        from   vwPerson
                        where  BaPersonID = {0}",
                        qryKbBuchung["BaPersonID"]);

                    qryKbBuchung["MitteilungZeile1"] = TruncateString(qry2["Text"], 35);
                    qryKbBuchung["ReferenzNummer"] = DBNull.Value;
                }
                else
                {
                    qryKbBuchung["MitteilungZeile1"] = DBNull.Value;
                    qryKbBuchung["MitteilungZeile2"] = DBNull.Value;
                    qryKbBuchung["MitteilungZeile3"] = DBNull.Value;
                }
            }

            qryKbBuchung.RefreshDisplay();
            SetEditMode();
        }

