        private bool CreateWikBetreibungsSollstellungen(DataRow row, int counterBearbeitet)
        {
            string UserText = string.Format("Sollstellungsbeleg {0}/{1}  {2}; CHF {3:N2}", counterBearbeitet, TotalAnzahl, row["Schuldner"], row["Betrag"]);
            DlgProgressLog.AddLine(UserText + ": erstellen ... ");
            try
            {
                qrySollstellung.Fill(-1); // Fill with nothing

                Session.BeginTransaction();
                qrySollstellung.Insert();

                qrySollstellung["FaLeistungID"] = row["FaLeistungID"];
                qrySollstellung["IkRechtstitelID"] = null;
                qrySollstellung["Einmalig"] = 1;
                qrySollstellung["Datum"] = row["ValutaDatum"]; // DatumPer
                // DatumPer wird laut CR aus Verwendungsperiode (VP) bestimmen,
                // und VP-von und VP-bis wird durch das ValutaDatum bestimmt
                qrySollstellung["Monat"] = ((DateTime)qrySollstellung["Datum"]).Month; //wird durch Feld Datum bestimmt
                qrySollstellung["Jahr"] = ((DateTime)qrySollstellung["Datum"]).Year; //wird durch Feld Datum bestimmt
                qrySollstellung["BaPersonID"] = null; // Gläubiger row["BaPersonID"];
                qrySollstellung["BetragEinmalig"] = row["Betrag"];
                string koaOptions = DBUtil.GetConfigString(@"System\WH\Wik_AbbildungLABetreibungssollstellung", null);
                Regex r = new Regex(@"(?<value>\d+)\s*:\s*(?<key>\d+)");
                foreach (string koaOption in koaOptions.Split(','))
                {
                    Match m = r.Match(koaOption);
                    string key = m.Groups[1].Value;
                    string value = m.Groups[2].Value;
                    if (row["KOA"].Equals(key))
                    {
                        int forderungEinmaligCode = (int)DBUtil.ExecuteScalarSQLThrowingException(
                            string.Format(@"SELECT LOV.Code FROM BgKostenart KOS
                                            LEFT JOIN XLOVCode LOV ON LOV.Value2 = Convert(varchar(25), KOS.BgKostenartid) AND LOV.LOVName = 'IkForderungEinmalig'
                                            WHERE KOS.KontoNr = '{0}'", value)); // Konvertierung auf varchar, da bei Konvertierung des value2-Wertes anderer Zeilen auf int
                        // eine Konvertierungs-Exception auftreten kann
                        qrySollstellung["IkForderungEinmaligCode"] = forderungEinmaligCode;
                        break;
                    }
                }

                if (DBUtil.IsEmpty(qrySollstellung["IkForderungEinmaligCode"]))
                {
                    throw new Exception("Konfigurations-Fehler: Es ist kein Forderungscode für diese LA hinterlegt.");
                }
                qrySollstellung["BetragIstNegativ"] = 0;
                qrySollstellung["IndexStandBeiBerechnung"] = null;
                qrySollstellung["IndexStandDatum"] = null;
                qrySollstellung["Bemerkung"] = string.Format("Betrifft Buc.: {0}", row["KbBuchungID"]);
                qrySollstellung["Forderung_DocumentID"] = null;
                qrySollstellung["VerwPeriodeVon"] = null; // row["VerwPeriodeVon"];
                qrySollstellung["VerwPeriodeBis"] = null; // row["VerwPeriodeBis"];
                qrySollstellung["IkBuchungsartCode"] = 0; // Forderung
                qrySollstellung["Buchungstext"] = row["Text"];
                qrySollstellung["DossierNummer"] = row["Dossiernummer"];
                int stringLenght = ((string)row["Text"]).Length > 20 ? 20 : ((string)row["Text"]).Length;
                qrySollstellung["VerfahrensNummer"] = ((string)row["Text"]).Substring(0, stringLenght);
                qrySollstellung["ErstellungsGrund"] = string.Format("Betreibungsrechnung: {0}", row["KbBuchungID"]);
                // Die KbBuchungID, die das automatische Erstellen der Sollstellung auslöst

                qrySollstellung.Post();
                // Sollstellungsbuchung erstellen
                string errorText = string.Empty;
                SqlQuery qry = DBUtil.OpenSQL(string.Format(@"
                                    EXEC dbo.spIkSollstellung_KbBuchung_Create {0}, '{1}', {2}, {3}",
                    qrySollstellung["IkPositionID"],
                    //((DateTime)valutaDatum).ToString(),
                    ((DateTime)row["ValutaDatum"]).ToString("yyyyMMdd"),
                    Session.User.UserID,
                    0
                 ));
                bool success = (Utils.ConvertToInt(qry["HasErrors"]) == 0);

                if (success)
                {
                    Session.Commit();
                    DlgProgressLog.UpdateLastLine(UserText + ": erfolgreich");
                    return true;
                }
                else
                {
                    if (Session.Transaction != null)
                    {
                        Session.Rollback();
                    }
                    if (qry.DataTable.Columns.Contains("ErrorText") && !DBUtil.IsEmpty(qry["ErrorText"]))
                    {
                        errorText = Utils.ConvertToString(qry["ErrorText"]);
                    }
                    throw new Exception(errorText);
                }
            }
            catch (Exception ex)
            {
                if (Session.Transaction != null)
                {
                    Session.Rollback();
                }
                DlgProgressLog.UpdateLastLine(UserText + ": fehlerhaft");
                DlgProgressLog.AddError(ex.Message);
                if (!DBUtil.IsEmpty(ex.Message))
                {
                    XLog.Create(GetType().FullName, LogLevel.ERROR, ex.Message);
                }
                if (!DBUtil.IsEmpty(ex.InnerException) && !DBUtil.IsEmpty(ex.InnerException.Message))
                {
                    XLog.Create(GetType().FullName, LogLevel.ERROR, ex.InnerException.Message);
                }
                return false;
            }
        }

        private void ProgressEnd()
        {
            qryKbBuchung.Refresh();
        }

        private void ProgressStart()
        {
            Cursor = Cursors.WaitCursor;
            ApplicationFacade.DoEvents();

            DlgProgressLog.AddLine("Start der Übertragung");
            ApplicationFacade.DoEvents();
            int counterBearbeitet = 0;
            int successCountBetreibungBuc = 0;
            int successCountSollstellung = 0;
            int counterWikSollstellungen = 0;

            try
            {
                DBUtil.ThrowExceptionOnOpenTransaction();
                foreach (DataRow row in qryKbBuchung.DataTable.Rows)
                {
                    if (!DBUtil.IsEmpty(row["Sel"]) && (bool)row["Sel"])
                    {
                        // Betreibungs-Buchungen erstellen
                        string UserText = string.Format("Beleg {0}/{1}  {2}; CHF {3:N2}", counterBearbeitet + 1, TotalAnzahl, row["Schuldner"], row["Betrag"]);
                        DlgProgressLog.AddLine(UserText + ": senden ... ");
                        try
                        {
                            bool success = KiSS4.FAMOZ.PSCDServices.PSCDInterface.SubmitAlimBuchungsstoff(new int[] { (int)row["KbBuchungID"] });

                            if (success)
                            {
                                DlgProgressLog.UpdateLastLine(UserText + ": erfolgreich");
                                successCountBetreibungBuc++;
                            }
                            else
                            {
                                DlgProgressLog.UpdateLastLine(UserText + ": fehlerhaft");
                            }
                        }
                        catch (Exception ex)
                        {
                            DlgProgressLog.AddError(UserText + ": " + ex.Message);
                            continue;
                        }
                        finally
                        {
                            counterBearbeitet++;
                        }

                        int? faProzessCode = row["FaProzessCode"] as int?;
                        bool wik = false; // ist W-Inkasso-Buchung
                        if (faProzessCode == 301 || faProzessCode == 302 || faProzessCode == 304)
                        {
                            wik = true;
                            counterWikSollstellungen += 1;
                        }
                        if (wik) // für wik: Erstelle Sollstellung und Sollstellungsbuchung
                        {
                            if (this.CreateWikBetreibungsSollstellungen(row, counterBearbeitet))
                            {
                                successCountSollstellung++;
                            }
                        }
                    }
                }
                DlgProgressLog.AddLine("Ende der Übertragung");
                DlgProgressLog.AddLine(string.Format("- {0} Beleg(e) erfolgreich übermittelt", successCountBetreibungBuc));
                DlgProgressLog.AddLine(string.Format("- {0} Beleg(e) fehlerhaft", counterBearbeitet - successCountBetreibungBuc));
                if (counterWikSollstellungen > 0)
                {
                    DlgProgressLog.AddLine(string.Format("- {0} Sollstellungen wurden erfolgreich erstellt", successCountSollstellung));
                    DlgProgressLog.AddLine(string.Format("- {0} Sollstellungen konnten nicht erstellt werden", counterWikSollstellungen - successCountSollstellung));
                }
            }
            catch (CancelledByUserException)
            {
                string msg = "Abbruch durch Benutzer/in\r\n\r\n" +
                                 string.Format("- {0} Beleg(e) erfolgreich übermittelt", successCountBetreibungBuc) + "\r\n" +
                                 string.Format("- {0} Beleg(e) fehlerhaft", counterBearbeitet - successCountBetreibungBuc) + "\r\n";
                if (counterWikSollstellungen > 0)
                {
                    msg += string.Format("- {0} Sollstellungen wurden erfolgreich erstellt", successCountSollstellung) + "\r\n" +
                           string.Format("- {0} Sollstellungen konnten nicht erstellt werden", counterWikSollstellungen - successCountSollstellung) + "\r\n";
                }
                KissMsg.ShowInfo(msg);
            }

            Cursor = Cursors.Default;

            DlgProgressLog.EndProgress();
            DlgProgressLog.ShowTopMost();
        }

        private void SetEditMode()
        {
            int status = ShUtil.GetCode(qryKbBuchung["KbBuchungStatusCode"]);
            int ES = ShUtil.GetCode(qryKbBuchung["EinzahlungsscheinCode"]);
            int BgSplittingArtCode = ShUtil.GetCode(qryKbBuchung["BgSplittingArtCode"]);

            bool editable = (status == 2 || status == 5);  //grün, Zahlauftrag fehlerhaft

            qryKbBuchung.EnableBoundFields(editable);
            btnTransferPSCD.Enabled = editable;

            //Verwendungsperiode + Splitting
            switch (BgSplittingArtCode)
            {
                case 1: //Taggenau
                case 2: //monat
                    edtVerwPeriodeVon.EditMode = editable ? EditModeType.Normal : EditModeType.ReadOnly;
                    edtVerwPeriodeBis.EditMode = editable ? EditModeType.Normal : EditModeType.ReadOnly;
                    break;

                case 4: //Entscheid
                    edtVerwPeriodeVon.EditMode = editable ? EditModeType.Normal : EditModeType.ReadOnly;
                    edtVerwPeriodeBis.EditMode = EditModeType.ReadOnly;
                    break;

                default:
                    edtVerwPeriodeVon.EditMode = EditModeType.ReadOnly;
                    edtVerwPeriodeBis.EditMode = EditModeType.ReadOnly;
                    break;
            }

            //Positionsbutton Bewilligung,Verlauf,Spezialkonto
            edtReferenzNummer.EditMode = (ES == 1) && editable ? EditModeType.Normal : EditModeType.ReadOnly;
            edtMitteilungZeile1.EditMode = ((ES == 2) || (ES == 3) || (ES == 5)) && editable ? EditModeType.Normal : EditModeType.ReadOnly;
            edtMitteilungZeile2.EditMode = edtMitteilungZeile1.EditMode;
        }

        private void SetVerwendungsPeriode()
        {
            try
            {
                int BgSplittingArtCode = ShUtil.GetCode(qryKbBuchung["BgSplittingArtCode"]);
                switch (BgSplittingArtCode)
                {
                    case 3: //Valuta
                    case 4: //Entscheid
                        qryKbBuchung["VerwPeriodeVon"] = DBNull.Value;
                        qryKbBuchung["VerwPeriodeBis"] = DBNull.Value;
                        break;
                }
            }
            catch { }
        }

        private object TruncateString(object s, int MaxLength)
        {
            if (DBUtil.IsEmpty(s)) return s;
            if (!(s is String)) return s;
            if (s.ToString().Length > MaxLength) return s.ToString().Substring(0, MaxLength);
            return s;
        }

