		#region Private Properties

		// ComponentName: qryQuery
		private int KgBudgetID
		{
			get { return (int)qryQuery["KgBudgetID"]; }
		}

		#endregion

		#region Public Methods

		public override void OnSearch()
		{
			if (tabControlSearch.SelectedTab == tpgSuchen)
			{
				foreach (Control ctl in UtilsGui.AllControls(tpgSuchen))
				{
					if (ctl is KissButtonEdit)
					{
						KissButtonEdit edt = (KissButtonEdit)ctl;
						edt.CheckPendingSearchValue();
					}
				}
			}

			// Workaround um F3 weiter zu reichen, da das FrameWork das OrgUnitTeamUser UserControl nicht kennt
			try
			{
				if (((ContainerControl)ActiveControl).ActiveControl.Equals(ctlOrgUnitTeamUser))
					edtSucheKlient.Focus();
			}
			catch
			{
			}

			base.OnSearch();
		}

		#endregion

		#region Private Methods

		private void btnJumpTo_Click(object sender, System.EventArgs e)
		{
			if (!DBUtil.IsEmpty(qryQuery["KgBudgetID"]))
			{
				System.Collections.Specialized.HybridDictionary dict = new System.Collections.Specialized.HybridDictionary();
				dict.Add("Action", "JumpToPath");
				dict.Add("BaPersonID", qryQuery["BaPersonID"]);
				dict.Add("ModulID", 5);
				dict.Add("TreeNodeID", string.Format(@"CtlKgLeistung{0}\Masterbudget{1}", qryQuery["FaLeistungID"], qryQuery["KgBudgetID"]));
				if (!DBUtil.IsEmpty(qryQuery["KgPositionID"]))
					dict.Add("ActiveSQLQuery.Find", string.Format("KgPositionID={0}", qryQuery["KgPositionID"]));

				FormController.OpenForm("FrmFall", dict);
			}
		}

		#endregion

        private void btnVisieren_Click(object sender, EventArgs e)
        {
            if (qryQuery.Count == 0) return;
            
            try
            {
                Session.BeginTransaction();
                DBUtil.ExecSQLThrowingException(@"
                    EXEC spKgPositionBewilligen {0}, {1}
                    ", qryQuery["KgPositionID"], Session.User.UserID);
                //ev. Erfolgsmeldung für Benutzer nötig  (nicht spezifiziert)
                qryQuery.Delete("");
                Session.Commit();
            }
            catch (Exception ex)
            {
                if (Session.Transaction != null)
                {
                    Session.Rollback();
                }
                KissMsg.ShowError("Fehler beim Visieren der Masterbudgetposition", ex);
            }
        }

        private void qryQuery_PositionChanged(object sender, EventArgs e)
        {
            btnVisieren.Enabled = !DBUtil.IsEmpty(qryQuery["KgPositionID"]) &&
                                  (((int)qryQuery["UserID"] == Session.User.UserID) ||
                                   DBUtil.UserHasRight("DlgKgMasterbudgetVisieren", "I"));
        }

        #region Protected Methods

        protected override void NewSearch()
        {
            base.NewSearch();
            ctlOrgUnitTeamUser.NewSearch();
        }

        #endregion

