        private void CtlFibuBankPost_Search(object sender, System.EventArgs e)
        {
            if (this.xTabControl1.SelectedTabIndex == 0)
            {
                if (this.qryBank.Post())
                {
                    NeueSuche();
                    this.xTabControl1.SelectedTabIndex = 1;
                }
            }
            else
            {
                Suchen();
            }
        }

        private void btnBankHistory_Click(object sender, System.EventArgs e)
        {
            KissDialog dlg = new DlgBankenabgleich();
            dlg.ShowDialog();
            SetLastUpdateText();
        }

        private void btnUpdate_Click(object sender, System.EventArgs e)
        {
            KissDialog dlg = new DlgBankenabgleich(true);
            dlg.ShowDialog();
            SetLastUpdateText();
        }

        private void CtlKgBankPost_Load(object sender, System.EventArgs e)
        {
            qryBank.Fill("select B.* from BaBank B order by B.Name, B.PLZ");
            grdBaBank.Focus();
            SetLastUpdateText();
        }

        private void grvBaBank_CustomDrawCell(object sender, DevExpress.XtraGrid.Views.Base.RowCellCustomDrawEventArgs e)
        {
            e.Handled = false;
            if (grvBaBank.FocusedRowHandle == e.RowHandle) return;

            bool isSicUpdated = Utils.ConvertToBool(grvBaBank.GetRowCellValue(e.RowHandle, grvBaBank.Columns["SicUpdated"]), false);
            if (isSicUpdated)
            {
                e.Appearance.BackColor = Color.Bisque;
            }
            else
            {
                e.Appearance.BackColor = Color.LightSalmon;
            }

            bool hasClearingNrNeu = null != grvBaBank.GetRowCellValue(e.RowHandle, grvBaBank.Columns["ClearingNrNeu"]) as int?;
            if (hasClearingNrNeu && isSicUpdated)
            {
                e.Appearance.BackColor = Color.AliceBlue;
            }
        }

        private void SetLastUpdateText()
        {
            lblLastUpdate.Text = "letzte Aktualisierung am " + DlgBankenabgleich.GetLastUpdateDate().ToString();
        }

        private void NeueSuche()
        {
            this.editPCKontoNrSearch.Text = "";
            this.editNameSearch.Text = "";
            this.editClearingNrSearch.Text = "";
            this.editPLZSearch.Text = "";
            this.editOrtSearch.Text = "";
            this.editStrasseSearch.Text = "";
            this.editZusatzSearch.Text = "";
            this.editPCKontoNrSearch.Focus();
        }

        private void Suchen()
        {
            qryBank.SelectStatement = "Select B.* from BaBank B where 1 = 1 ";

            // Kreditor Search Params
            if (!(bool)edtAuchInaktiveBanken.EditValue)
            {
                qryBank.SelectStatement += " and B.ClearingNrNeu IS NULL ";
            }
            if (!DBUtil.IsEmpty(editNameSearch.Text))
            {
                qryBank.SelectStatement += " and  B.Name like '%" + this.editNameSearch.Text + "%' ";
            }
            if (!DBUtil.IsEmpty(editZusatzSearch.Text))
            {
                qryBank.SelectStatement += " and  B.Zusatz like '%" + this.editZusatzSearch.Text + "%' ";
            }
            if (!DBUtil.IsEmpty(editStrasseSearch.Text))
            {
                qryBank.SelectStatement += " and  B.Strasse like '%" + this.editStrasseSearch.Text + "%' ";
            }
            if (!DBUtil.IsEmpty(editPLZSearch.Text))
            {
                qryBank.SelectStatement += " and  B.PLZ like '%" + this.editPLZSearch.Text + "%' ";
            }
            if (!DBUtil.IsEmpty(editOrtSearch.Text))
            {
                qryBank.SelectStatement += " and  B.Ort like '%" + this.editOrtSearch.Text + "%' ";
            }
            if (!DBUtil.IsEmpty(editClearingNrSearch.Text))
            {
                qryBank.SelectStatement += " and  B.ClearingNr = '" + this.editClearingNrSearch.Text + "' ";
            }
            if (!DBUtil.IsEmpty(editPCKontoNrSearch.Text))
            {
                qryBank.SelectStatement += " and  B.PCKontoNr like '%" + this.editPCKontoNrSearch.Text + "%' ";
            }

            qryBank.SelectStatement += " Order by B.Name, B.PLZ";

            qryBank.Fill();
            qryBank.RefreshDisplay();
            this.xTabControl1.SelectedTabIndex = 0;
        }

        private void kissButtonEdit1_FormatEditValue(object sender, DevExpress.XtraEditors.Controls.ConvertEditValueEventArgs e)
        {
            try
            {
                e.Handled = true;
                e.Value = Utils.FormatPCKontoNummerToUserFormat(e.Value.ToString().Replace("*", ""));
            }

            catch (Exception f)
            {
                Console.Write(f.Message);
            }
        }

        private void kissButtonEdit1_ParseEditValue(object sender, DevExpress.XtraEditors.Controls.ConvertEditValueEventArgs e)
        {
            try
            {
                e.Handled = true;
                e.Value = Utils.FormatPCKontoNummerToDBFormat(e.Value.ToString());
            }
            catch (Exception f)
            {
                Console.Write(f.Message);
            }
        }

