        public void Init(string Name, Image TitleImage, int FaLeistungID, int BaPersonID)
        {
            lblTitel.Text = Name;
            picTitel.Image = TitleImage;
            faLeistungID = FaLeistungID;
            baPersonID = BaPersonID;
        }

