        #region Private Methods

        private void btnDrucken_Click(object sender, System.EventArgs e)
        {
            if (qryPeriode.Count == 0) return;

            NamedPrm[] prms = new NamedPrm[3];
            prms[0] = new NamedPrm("edtBaPersonID", edtBaPersonID.EditValue);
            prms[1] = new NamedPrm("edtDatumVon", edtDatumVon.EditValue);
            prms[2] = new NamedPrm("edtDatumBis", edtDatumBis.EditValue);

            RepUtil.ExecuteReport("KgJournal", KissReportDestination.Viewer, prms);
        }

        private void CtlKgJournal_Load(object sender, System.EventArgs e)
        {
            //Buchungsstati laden
            repositoryItemImageComboBox1.SmallImages = KissImageList.SmallImageList;
            SqlQuery qryStati = DBUtil.OpenSQL("select * from XLOVCode where LOVName = 'KgBuchungsStatus' order by SortKey");
            foreach (DataRow R in qryStati.DataTable.Rows)
            {
                repositoryItemImageComboBox1.Items.Add(new DevExpress.XtraEditors.Controls.ImageComboBoxItem(
                    R["Text"].ToString(),
                    (int)R["Code"],
                    KissImageList.GetImageIndex(Convert.ToInt32(R["Value1"]))));
            }

            colTyp.ColumnEdit = grdPeriode.GetLOVLookUpEdit(DBUtil.OpenSQL(@"
                            select Code, Text = ShortText
                            from   XLOVCode
                            where  LOVName = 'KgBuchungTyp'"));

            lblMandant.Text = "";

            this.gridView1.Appearance.HideSelectionRow.Options.UseFont = false;
            this.gridView1.Appearance.FocusedRow.Options.UseFont = false;
            this.gridView1.Appearance.FocusedCell.Options.UseFont = false;

            if (baPersonID > 0)
            {
                PresetSearchValues();
                edtMandant.EditMode = EditModeType.ReadOnly;
                RunSearch();
            }
            else
            {
                edtMandant.EditMode = EditModeType.Normal;
                NewSearch();
            }
        }

        private void DisplayPerioden()
        {
            qryPeriode.Fill(baPersonID);
            try
            {
                while (!((DateTime)qryPeriode["PeriodeVon"] <= DateTime.Today && (DateTime)qryPeriode["PeriodeBis"] >= DateTime.Today))
                    if (!qryPeriode.Next()) break;
            }
            catch { }
        }

        private void edtMandant_UserModifiedFld(object sender, KiSS4.Gui.UserModifiedFldEventArgs e)
        {
            string SearchString = edtMandant.EditValue.ToString().Replace("*", "%").Replace("?", "_").Replace(" ", "%");

            if (DBUtil.IsEmpty(SearchString))
            {
                if (e.ButtonClicked)
                {
                    SearchString = "%";
                }
                else
                {
                    baPersonID = -1;
                    edtMandant.EditValue = null;
                    edtBaPersonID.EditValue = null;
                    edtAdresse.EditValue = null;
                    edtMT.EditValue = null;
                    qryPeriode.DataTable.Rows.Clear();
                    qryPeriode.EnableBoundFields(false);
                    return;
                }
            }

            KissLookupDialog dlg = new KissLookupDialog();
            e.Cancel = !dlg.SearchData(@"
              SELECT distinct
                     BaPersonID$      = PRS.BaPersonID,
                     [Personen-Nr.]   = PRS.BaPersonID,
                     Name             = PRS.NameVorname,
                     Geburtsdatum     = PRS.Geburtsdatum,
                     [Alter]          = PRS.[Alter],
                     Adresse          = PRS.Wohnsitz,
                     [Beist. oder Vorm.] = dbo.fnVmGetMTName(PRS.BaPersonID)
              FROM   vwPerson PRS
                     inner join FaLeistung LEI on LEI.BaPersonID = PRS.BaPersonID and
                                                  LEI.FaProzessCode = 500
              WHERE  PRS.NameVorname LIKE '%' + {0} + '%'
              ORDER BY PRS.NameVorname",
              SearchString,
              e.ButtonClicked, null, null, null);

            if (!e.Cancel)
            {
                baPersonID = (int)dlg["BaPersonID$"];
                edtMandant.EditValue = dlg["Name"];
                edtBaPersonID.EditValue = dlg["BaPersonID$"];
                edtAdresse.EditValue = dlg["Adresse"];
                edtMT.EditValue = dlg["Beist. oder Vorm."];

                DisplayPerioden();
                SetRanges();
            }
        }

        private void PresetSearchValues()
        {
            SqlQuery qry = DBUtil.OpenSQL(@"
              SELECT Name    = PRS.NameVorname,
                     Adresse = PRS.Wohnsitz,
                     [Beist. oder Vorm.] = fnVmGetMTName(PRS.BaPersonID)
              FROM   vwPerson PRS
              WHERE  PRS.BaPersonID = {0}",
              baPersonID);

            edtBaPersonID.EditValue = baPersonID;
            edtMandant.EditValue = qry["Name"];
            edtAdresse.EditValue = qry["Adresse"];
            edtMT.EditValue = qry["Beist. oder Vorm."];

            DisplayPerioden();
            SetRanges();
        }

        private void qryPeriode_PositionChanged(object sender, System.EventArgs e)
        {
            SetRanges();
        }

        private void SetRanges()
        {
            if (qryPeriode.Count == 0) return;

            edtDatumVon.DateTime = (DateTime)qryPeriode["PeriodeVon"];
            edtDatumBis.DateTime = (DateTime)qryPeriode["PeriodeBis"];
        }

        #endregion
    }
}