        #region EventHandlers

        private void edtFilter_EditValueChanged(object sender, EventArgs e)
        {
            string userFilter = edtFilter.EditValue as string;
            userFilter = userFilter.Replace("*", "[*]"); // Escapen von Wildcards, die werden innerhalb einer Expression nicht unterstützt.
            userFilter = userFilter.Replace("%", "[%]");
            string[] userFilterParts = userFilter.Split(' ');
            string filter = string.Empty;
            foreach (string filterPart in userFilterParts)
            {
                if (!filterPart.Trim().Equals(string.Empty))
                {
                    filter = string.Format(filter.Equals(string.Empty) ? "NameVorname LIKE '%{0}%'" : filter + " AND NameVorname LIKE '%{0}%'", filterPart);
                }
            }

            try
            {
                this.qryQuery.DataTable.DefaultView.RowFilter = filter;
            }
            catch (System.Data.EvaluateException)
            {
                KissMsg.ShowInfo("Ungültiger Filterausdruck");
            }
            fillQryDocument();
        }

        private void edtKontoBis_Validated(object sender, EventArgs e)
        {
            if (DBUtil.IsEmpty(edtKontoBis.EditValue))
                edtKontoBisBeschrieb.Text = string.Empty;
            else
            {
                object kontoName = DBUtil.ExecuteScalarSQL(@"
                select	KontoName
                from	KgKonto
                where	KontoNr = {0} AND KgPeriodeID IS NULL AND KontoGruppe = 0 AND KgKontoKlasseCode IN (1,2)",// (1,2) = aktive/passive
                    edtKontoBis.EditValue);
                edtKontoBisBeschrieb.EditValue = kontoName;
            }
        }

        private void edtKontoVon_Validated(object sender, EventArgs e)
        {
            if (DBUtil.IsEmpty(edtKontoVon.EditValue))
                edtKontoVonBeschrieb.Text = string.Empty;
            else
            {
                object kontoName = DBUtil.ExecuteScalarSQL(@"
                select	KontoName
                from	KgKonto
                where	KontoNr = {0} AND KgPeriodeID IS NULL AND KontoGruppe = 0 AND KgKontoKlasseCode IN (1,2)",// (1,2) = aktive/passive
                    edtKontoVon.EditValue);
                edtKontoVonBeschrieb.EditValue = kontoName;
                if (DBUtil.IsEmpty(edtKontoBis.EditValue))
                    edtKontoBis.EditValue = edtKontoVon.EditValue;
            }
        }

        private void qryDocument_AfterInsert(object sender, EventArgs e)
        {
            qryDocument["Stichtag"] = edtStichtag.EditValue;
            edtDokStichtag.EditValue = edtStichtag.EditValue;
            qryDocument["KgKontoID"] = qryQuery["KgKontoID"];
            qryDocument["KgDokumentTypCode"] = 5; // Konto
            qryDocument["Stichwort"] = "Kontoauszug - " + edtStichtag.DateTime.ToString("dd.MM.yyyy");
            qryDocument.RowModified = true;
        }

        private void qryQuery_PositionChanged(object sender, EventArgs e)
        {
            qryDocument.Post();
            fillQryDocument();
            this.SetEditMode();
        }

        private void tabControlSearch_SelectedTabChanged(SharpLibrary.WinControls.TabPageEx page)
        {
            if (page == this.tpgSuchen)
                this.lblStichtagHeader.Text = string.Empty;
            else if (!DBUtil.IsEmpty(edtStichtag.EditValue))
                this.lblStichtagHeader.Text = "Stichtag: " + ((DateTime)this.edtStichtag.EditValue).ToString("dd.MM.yyyy");
        }

        private void xDocument_DocumentImporting(object sender, CancelEventArgs e)
        {
            qryDocument["Stichwort"] = edtText.Text;
        }

        private void fillQryDocument()
        {
            if (this.qryQuery.Position >= 0)
            {
                this.qryDocument.Fill((int)this.qryQuery["KgKontoID"]);
            }
            else
            {
                this.qryDocument.Fill(-1);
            }
        }

