        #region Methods

        #region Public Methods

        public override bool OnAddData()
        {
            qryDocument.Post();

            if (this.qryQuery.Position >= 0 && tabControlSearch.SelectedTab == tpgListe)
            {
                bool inserted = (this.qryDocument.Insert() != null);
                this.SetEditMode();
                return inserted;
            }
            return false;
        }

        public override bool OnDeleteData()
        {
            if (qryDocument.Position >= 0 && tabControlSearch.SelectedTab == tpgListe)
            {
                bool deleted = this.qryDocument.Delete();
                this.SetEditMode();
                return deleted;
            }
            return false;
        }

        public override bool OnSaveData()
        {
            if (qryDocument.Position >= 0)
                return this.qryDocument.Post();
            return true; // let pretend it works, kissSearch wouldn't like it the other way
        }

        public override void OnSearch()
        {
            if (tabControlSearch.SelectedTab == tpgSuchen)
            {
                foreach (Control ctl in UtilsGui.AllControls(tpgSuchen))
                {
                    if (ctl is KissButtonEdit)
                    {
                        KissButtonEdit edt = (KissButtonEdit)ctl;
                        edt.CheckPendingSearchValue();
                    }

                    if (ctl is CtlOrgUnitTeamUser)
                    {
                        ((CtlOrgUnitTeamUser)ctl).CheckPendingSearchValue();
                    }
                }
            }

            base.OnSearch();
        }

        #endregion

        #region Protected Methods

        protected override void NewSearch()
        {
            base.NewSearch();

            edtKontoVon.Focus();
        }

        protected override void OnLoad(EventArgs e)
        {
            base.OnLoad(e);
            this.grdDok.DataSource = this.qryDocument;
        }

        protected override void RunSearch()
        {
            if (DBUtil.IsEmpty(edtKontoBis.EditValue))
            {
                edtKontoBis.EditValue = edtKontoVon.EditValue;
            }

            if (DBUtil.IsEmpty(edtKontoVon.EditValue))
            {
                KissMsg.ShowInfo(string.Format("Das Feld {0} muss ausgefüllt werden.", lblKontoVon.Text));
                throw new KissCancelException();
            }
            if (DBUtil.IsEmpty(edtStichtag.EditValue))
            {
                KissMsg.ShowInfo(string.Format("Das Feld {0} muss ausgefüllt werden.", lblStichtag.Text));
                throw new KissCancelException();
            }

            qryQuery.Fill();
            base.RunSearch();
        }

