        #region Private Methods

        private void SetEditMode()
        {
            if (this.qryQuery.Position >= 0 && qryDocument.Count > 0)
            {
                this.edtDokStichtag.EditMode = EditModeType.Normal;
                this.edtText.EditMode = EditModeType.Normal;
                this.xDocument.EditMode = EditModeType.Normal;
            }
            else
            {
                this.edtDokStichtag.EditMode = EditModeType.ReadOnly;
                this.edtText.EditMode = EditModeType.ReadOnly;
                this.xDocument.EditMode = EditModeType.ReadOnly;
            }
        }

