        private void btnAlle_Click(object sender, System.EventArgs e)
        {
            Cursor = Cursors.WaitCursor;

            foreach (DataRow R in qryKgBuchung.DataTable.Rows)
            {
                if (!DBUtil.IsEmpty(R["Sel"])) R["Sel"] = true;
            }
            CalculateTotal();
            Cursor = Cursors.Default;
        }

        private void btnKeine_Click(object sender, System.EventArgs e)
        {
            Cursor = Cursors.WaitCursor;

            foreach (DataRow R in qryKgBuchung.DataTable.Rows)
            {
                if (!DBUtil.IsEmpty(R["Sel"])) R["Sel"] = false;
            }
            CalculateTotal();
            Cursor = Cursors.Default;
        }

        private void btnMonatsbudget_Click(object sender, System.EventArgs e)
        {
            if (qryKgBuchung.Count == 0 || DBUtil.IsEmpty(qryKgBuchung["JumpToMBPfad"])) return;

            FormController.OpenForm("FrmFall", "Action", "JumpToNodeByFieldValue",
                        "BaPersonID", qryKgBuchung["FallBaPersonID"],
                        "ModulID", 5,
                        "FieldValue", qryKgBuchung["JumpToMBPfad"]);
        }

        private void btnTransfer_Click(object sender, System.EventArgs e)
        {
            System.Collections.Generic.List<int> IDList = new System.Collections.Generic.List<int>();

            TotalBetrag = 0;
            foreach (DataRow R in qryKgBuchung.DataTable.Rows)
            {
                if (!DBUtil.IsEmpty(R["Sel"]) && (bool)R["Sel"])
                {
                    IDList.Add((int)R["KgBuchungID"]);
                    TotalBetrag += (decimal)R["Betrag"];
                }
            }
            TotalAnzahl = IDList.Count;
            if (TotalAnzahl == 0) return;

            String msg;
            if (IDList.Count == 1)
                msg = string.Format("Soll 1 Auszahlbeleg mit dem Betrag von CHF {0:0,0.00} an PSCD übermittelt werden?", TotalBetrag);
            else
                msg = string.Format("Sollen {0} Auszahlbelege mit dem Total von CHF {1:0,0.00} an PSCD übermittelt werden?", TotalAnzahl, TotalBetrag);

            if (!KissMsg.ShowQuestion(msg)) return;

            DlgProgressLog.Show("Zahllauf", 800, 400, new ProgressEventHandler(ProgressStart), new ProgressEventHandler(ProgressEnd), Session.MainForm);
        }

        private void btnVorsaldieren_Click(object sender, System.EventArgs e)
        {
            if (!KissMsg.ShowQuestion("Soll die Vorsaldierung aller aktiven Perioden über allen Mandanten hinweg durchgeführt werden?")) return;

            try
            {
                DBUtil.ExecSQLThrowingException(600, @"
                    -- Vorsaldi vortragen für alle K-Mandanten
                      declare @BaPersonID int
                      declare @NameVorname varchar(200)

                      declare C cursor for
                      select distinct LEI.BaPersonID, PRS.NameVorname
                      from   FaLeistung LEI
                             inner join vwPersonSimple PRS on PRS.BaPersonID = LEI.BaPersonID
                      where  FaProzessCode = 500
                      order by PRS.NameVorname

                      open C
                      fetch next from C into @BaPersonID, @NameVorname
                      while @@fetch_status = 0 begin
                        print @NameVorname
                        exec spKgSaldiVortragen @BaPersonID
                        fetch next from C into @BaPersonID, @NameVorname
                      end
                      close C
                      deallocate C");
            }
            catch (Exception ex)
            {
                KissMsg.ShowInfo("Fehler beim Vorsaldieren aller Mandanten\r\n\r\n" + ex.Message);
                return;
            }

            KissMsg.ShowInfo("Die Vorsaldierung über alle Mandanten wurde durchgeführt.");
        }

        private void CalculateTotal()
        {
            int Count = 0;
            decimal Total = 0;

            foreach (DataRow R in qryKgBuchung.DataTable.Rows)
            {
                if (!DBUtil.IsEmpty(R["Sel"]) && (bool)R["Sel"])
                {
                    Count++;
                    Total += (decimal)R["Betrag"];
                }
            }

            edtTotal.EditValue = Total;
            edtAnzahl.EditValue = Count;

            btnTransfer.Enabled = (Count > 0);
            btnTransferAsync.Enabled = btnTransfer.Enabled;
        }

        private void CtlKgZahllauf_Load(object sender, System.EventArgs e)
        {
            colTyp.ColumnEdit = grdDoc.GetLOVLookUpEdit("KgDokumentTyp");

            //Buchungsstati laden
            repositoryItemImageComboBox1.SmallImages = KissImageList.SmallImageList;
            SqlQuery qryStati = DBUtil.OpenSQL("select * from XLOVCode where LOVName = 'KgBuchungsStatus' order by SortKey");
            foreach (DataRow R in qryStati.DataTable.Rows)
            {
                repositoryItemImageComboBox1.Items.Add(new DevExpress.XtraEditors.Controls.ImageComboBoxItem(
                    R["Text"].ToString(),
                    (int)R["Code"],
                    KissImageList.GetImageIndex(Convert.ToInt32(R["Value1"]))));
            }

            grvZahllauf.CellValueChanging += new DevExpress.XtraGrid.Views.Base.CellValueChangedEventHandler(grvZahllauf_CellValueChanging);
            grvZahllauf.CustomRowCellEdit += new DevExpress.XtraGrid.Views.Grid.CustomRowCellEditEventHandler(this.grvZahllauf_CustomRowCellEdit);

            NewSearch();
        }

