        {
            btnMonatsbudget.Enabled = !DBUtil.IsEmpty(qryKgBuchung["JumpToMBPfad"]);
            qryKgDokumente.Fill(qryKgBuchung["KgPositionID"]);

            // Negativen Saldo rot hinterlegen
